import { useEffect, useState, useRef } from "react";
import { Client } from "@stomp/stompjs";
import { useParams, useNavigate } from "react-router-dom";
import styles from "../styling/waitingRoom.css";
import Navbar2 from "./Navbar2";

const SOCKET_URL = "ws://localhost:8788/ws";

const WaitingRoom = () => {
  const { roomCode } = useParams();
  const navigate = useNavigate();
  const [players, setPlayers] = useState([]);
  const [timeLeft, setTimeLeft] = useState(120);
  const [isLoading, setIsLoading] = useState(true);
  const stompClientRef = useRef(null);

  useEffect(() => {
    if (stompClientRef.current) return;

    stompClientRef.current = new Client({
      brokerURL: SOCKET_URL,
      reconnectDelay: 5000,
      onConnect: () => {
        console.log("Connected to WebSocket");

        stompClientRef.current.subscribe(`/topic/room/${roomCode}`, (message) => {
          try {
            const updatedRoom = JSON.parse(message.body);

            setPlayers((prevPlayers) => {
              const newPlayers = new Set(prevPlayers); // Clone the previous state to avoid mutation
              (updatedRoom.players || []).forEach((player) => newPlayers.add(player)); // Add new players
              if (updatedRoom.host) newPlayers.add(updatedRoom.host); // Ensure host is included
              console.log(newPlayers);
              return Array.from(newPlayers); // Convert Set back to an array
            });


            setIsLoading(false);
          } catch (error) {
            console.error("Error parsing WebSocket message:", error);
          }
        });
      },
      onStompError: (frame) => console.error("WebSocket error:", frame),
      onWebSocketClose: () => console.log("WebSocket disconnected"),
    });

    stompClientRef.current.activate();

    return () => {
      if (stompClientRef.current) {
        stompClientRef.current.deactivate();
        stompClientRef.current = null;
      }
    };
  }, [roomCode]);

  useEffect(() => {
    if (timeLeft <= 0) {
      navigate("/");
      return;
    }
    const timer = setInterval(() => setTimeLeft((prev) => prev - 1), 1000);
    return () => clearInterval(timer);
  }, [timeLeft, navigate]);

  return (
    <div>
      <Navbar2 />
      <div className="waiting-room">
        <h2 className="title">Waiting for players</h2>
        <p className="room-code">Room Code: <strong>{roomCode}</strong></p>

        <div className="countdown">
          ⏳ Test starts in: <span className="timer">{timeLeft}s</span>
        </div>

        {isLoading ? (
          <div className="loading-container">
            <div className="spinner"></div>
            <p className="loading-text">Waiting for players...</p>
          </div>
        ) : (
          <>
            <h3 className="players-title">Players:</h3>
            <ul className="players-list">
              {players.length > 0 ? (
                players.map((player, index) => (
                  <li key={index} className="player-item">{player}</li>
                ))
              ) : (
                <p>No players joined yet.</p>
              )}
            </ul>
          </>
        )}

        <button className="cancel-button" onClick={() => navigate("/")}>
          Exit Room
        </button>
      </div>
    </div>
  );
};

export default WaitingRoom;
