import { useEffect, useState, useRef } from "react";
import { Client } from "@stomp/stompjs";
import { useNavigate } from "react-router-dom";
import Navbar2 from "./Navbar2";
import "../styling/waitingForRoomJoin.css";

const SOCKET_URL = "ws://localhost:8788/ws"; // Your WebSocket URL

const WaitingRoomJoins = () => {
  const queryParams = new URLSearchParams(window.location.search);
  const roomCode = queryParams.get("roomCode");
  const name = queryParams.get("name");

  // console.log("Extracted Room Code:", roomCode);
  // console.log("Extracted Name:", name);

  const navigate = useNavigate();
  const username = localStorage.getItem("Username"); // Get user's name

  const [players, setPlayers] = useState([]);
  const [host, setHost] = useState(""); // üîπ Track Host
  const [isLoading, setIsLoading] = useState(true);

  const stompClientRef = useRef(null);

  const [timeLeft, setTimeLeft] = useState(null); // Initially null

useEffect(() => {
  if (!roomCode) {
    console.error("‚ùå Room code is undefined! Subscription will fail.");
    return;
  }

  if (!stompClientRef.current) {
    stompClientRef.current = new Client({
      brokerURL: SOCKET_URL,
      onConnect: () => {
        stompClientRef.current.subscribe(`/topic/room/${roomCode}`, (message) => {
          const updatedRoom = JSON.parse(message.body);
          setHost(updatedRoom.host);
          let updatedPlayers = new Set(updatedRoom.players || []);
          updatedPlayers.add(updatedRoom.host);
          setPlayers([...updatedPlayers]);
          setTimeLeft(updatedRoom.timer); // Set initial timer from server
          setIsLoading(false);
        });
      },
    });
    stompClientRef.current.activate();
  }

  return () => {
    if (stompClientRef.current) {
      stompClientRef.current.deactivate();
      stompClientRef.current = null;
    }
  };
}, [roomCode]); // Add roomCode as dependency

// Countdown Logic
useEffect(() => {
  if (timeLeft === null) return; // Wait for server to set timeLeft
  if (timeLeft <= 0) {
    navigate("/");
    return;
  }

  const timer = setInterval(() => {
    setTimeLeft((prev) => (prev > 0 ? prev - 1 : 0));
  }, 1000);

  return () => clearInterval(timer);
}, [timeLeft, navigate]);

  // Handle Cancel Button Click
  const handleCancel = () => {
    navigate("/"); // Redirect to home page
  };

  return (
    <div>
      <Navbar2 />
      <div className="waiting-room">
        {/* Host Information */}
        <h2 className="host-title">
          üëë Host: <span className="host-name">{host || "Waiting..."}</span>
        </h2>

        {/* Room Code */}
        <p className="room-code">Room Code: <strong>{roomCode}</strong></p>

        {/* Welcome Status */}
        <div className="welcome-status">üéâ Welcome to the Game Room!</div>

        {/* Countdown Timer */}
        <div className="countdown">
          ‚è≥ Test starts in: <span className="timer">{timeLeft}s</span>
        </div>

        {/* Loading Animation */}
        {isLoading && (
          <div className="loading-container">
            <div className="spinner"></div>
            <p className="loading-text">Waiting for players...</p>
          </div>
        )}

        {/* Players List with Count */}
        <h3 className="players-title">Players in Room ({players.length}):</h3>
        <ul className="players-list">
          {players.map((player, index) => (
            <li key={index} className="player-item fade-in">
              {player === host ? `üëë ${player} (Host)` : player}
            </li>
          ))}
        </ul>

        {/* Exit Button */}
        <button className="cancel-button" onClick={handleCancel}>
          Exit Room
        </button>
      </div>
    </div>
  );
};

export default WaitingRoomJoins;
