import { useEffect, useState } from "react";
import { useParams, useNavigate } from "react-router-dom";
import io from "socket.io-client";

const socket = io("http://192.168.9.100:8080"); // Update with your backend WebSocket URL

const WaitingRoom = () => {
  const { roomCode } = useParams();
  const navigate = useNavigate();
  const [players, setPlayers] = useState([]);
  const [timer, setTimer] = useState(120); // 2 minutes countdown

  useEffect(() => {
    // Join the room in WebSocket
    socket.emit("joinRoom", roomCode);

    // Listen for player updates
    socket.on(`roomUpdate:${roomCode}`, (updatedRoom) => {
      setPlayers(updatedRoom.players);
    });

    // Countdown timer logic
    const countdown = setInterval(() => {
      setTimer((prev) => {
        if (prev === 1) {
          clearInterval(countdown);
          navigate(`/test/${roomCode}`); // Redirect to test page after 2 mins
        }
        return prev - 1;
      });
    }, 1000);

    return () => {
      clearInterval(countdown);
      socket.off(`roomUpdate:${roomCode}`);
    };
  }, [roomCode, navigate]);

  return (
    <div className="min-h-screen flex items-center justify-center bg-gray-900 text-white">
      <div className="bg-gray-800 p-6 rounded-lg shadow-lg w-96 text-center">
        <h2 className="text-2xl font-semibold">Waiting for Players...</h2>
        <p className="mt-2 text-sm">Room Code: <span className="font-bold">{roomCode}</span></p>

        {/* Timer */}
        <div className="text-lg font-semibold mt-4">
          Starting in: <span className="text-yellow-400">{Math.floor(timer / 60)}:{String(timer % 60).padStart(2, "0")}</span>
        </div>

        {/* Loading Spinner */}
        <div className="flex justify-center my-4">
          <div className="w-10 h-10 border-4 border-t-transparent border-blue-400 rounded-full animate-spin"></div>
        </div>

        {/* Player List */}
        <div className="mt-4">
          <h3 className="text-lg font-semibold">Players Joined:</h3>
          <ul className="mt-2">
            {players.length > 0 ? (
              players.map((player, index) => (
                <li key={index} className="mt-1">{player}</li>
              ))
            ) : (
              <p className="text-sm text-gray-400">Waiting for players...</p>
            )}
          </ul>
        </div>
      </div>
    </div>
  );
};

export default WaitingRoom;
