"use client"

import { useState, useEffect } from "react"
import { useRouter } from "next/navigation"
import { toast } from "react-hot-toast"
import Editor from "@monaco-editor/react"
import { CheckCircle, XCircle, Play, Send } from "lucide-react"

// Mock problems data - in a real app, you would import this
const problems = [
  {
    title: "Two Sum",
    description:
      "Given an array of integers nums and an integer target, return indices of the two numbers such that they add up to target.",
    category: "Arrays",
    inputFormat: "An array of integers and a target integer.",
    outputFormat: "An array of two integers representing the indices.",
    constraints: "2 <= nums.length <= 10^4, -10^9 <= nums[i] <= 10^9",
    testCases: [
      {
        input: "[2,7,11,15], 9",
        output: "[0,1]",
        expectedOutput: "[0,1]",
      },
      {
        input: "[3,2,4], 6",
        output: "[1,2]",
        expectedOutput: "[1,2]",
      },
      {
        input: "[3,3], 6",
        output: "[0,1]",
        expectedOutput: "[0,1]",
      },
    ],
  },
]

export default function PracticePage() {
  const router = useRouter()
  const [problem, setProblem] = useState(problems[0])
  const [code, setCode] = useState("")
  const [language, setLanguage] = useState("java")
  const [output, setOutput] = useState("")
  const [isRunning, setIsRunning] = useState(false)
  const [isSubmitting, setIsSubmitting] = useState(false)
  const [testResults, setTestResults] = useState([])
  const [currentTestIndex, setCurrentTestIndex] = useState(null)
  const [runningTestCase, setRunningTestCase] = useState(null)

  // Default starter code templates for different languages
  const starterCodeTemplates = {
    java: `class Solution {
    public int[] twoSum(int[] nums, int target) {
        // Your solution here
        return new int[]{0, 0};
    }
}`,
  }

  // Language ID mapping for the code execution API
  const languageIdMapping = {
    java: 62,
  }

  useEffect(() => {
    // Set initial starter code based on selected language
    setCode(starterCodeTemplates[language] || "")
  }, [language])

  // Change language and update starter code
  const handleLanguageChange = (newLanguage) => {
    setLanguage(newLanguage)
    setCode(starterCodeTemplates[newLanguage])
  }

  const handleCodeChange = (newCode) => {
    setCode(newCode)
  }

  // Function to run a single test case
  const runTestCase = async (testCase, index) => {
    setRunningTestCase({
      index,
      input: testCase.input,
      expected: testCase.expectedOutput,
      status: "running",
    })

    try {
      const token = sessionStorage.getItem("Authtoken")
      if (!token) throw new Error("Unauthorized")

      // Make the actual API call to your backend
      const response = await fetch("http://localhost:8788/api/coderun", {
        method: "POST",
        headers: {
          "Content-Type": "application/json",
          Authorization: `Bearer ${token}`,
        },
        body: JSON.stringify({
          code,
          language,
          testCaseInput: testCase.input,
        }),
      })

      const result = await response.json()

      // Compare result.output with expected output
      const isPassed = result.output.trim() === testCase.expectedOutput.trim()

      // Update the running test case with results
      setRunningTestCase((prev) => ({
        ...prev,
        output: result.output.trim(),
        status: isPassed ? "passed" : "failed",
        passed: isPassed,
      }))

      // Add to completed test results
      setTestResults((prev) => [
        ...prev,
        {
          input: testCase.input,
          expected: testCase.expectedOutput,
          output: result.output.trim(),
          passed: isPassed,
        },
      ])

      return isPassed
    } catch (error) {
      console.error(`Error on test case ${index + 1}:`, error)

      setRunningTestCase((prev) => ({
        ...prev,
        output: "Error executing code",
        status: "failed",
        passed: false,
      }))

      setTestResults((prev) => [
        ...prev,
        {
          input: testCase.input,
          expected: testCase.expectedOutput,
          output: "Error executing code",
          passed: false,
        },
      ])

      return false
    }
  }

  // Function to run all test cases sequentially
  const handleRunCode = async () => {
    setIsRunning(true)
    setTestResults([])
    setOutput("")

    const token = sessionStorage.getItem("Authtoken")
    if (!token) {
      toast.error("Unauthorized")
      setIsRunning(false)
      return
    }

    const testCases = problem.testCases || []

    for (let i = 0; i < testCases.length; i++) {
      setCurrentTestIndex(i)
      await runTestCase(testCases[i], i)
      // Small delay between test cases for better visual feedback
      await new Promise((resolve) => setTimeout(resolve, 300))
    }

    setCurrentTestIndex(null)
    setRunningTestCase(null)
    setIsRunning(false)
  }

  // Mock function to submit code
  const submitCode = async () => {
    if (!problem) {
      toast.error("No problem selected")
      return
    }

    setIsSubmitting(true)

    try {
      // Simulate API call
      await new Promise((resolve) => setTimeout(resolve, 2000))
      toast.success("Code submitted successfully!")
    } catch (error) {
      toast.error("Error submitting code")
      console.error(error)
    } finally {
      setIsSubmitting(false)
    }
  }

  if (!problem) {
    return <div className="flex items-center justify-center h-screen bg-[#1e1e1e] text-gray-200">Loading...</div>
  }

  return (
    <div className="min-h-screen bg-[#1e1e1e] text-gray-200 flex flex-col">
      <div className="bg-[#252525] p-3 border-b border-gray-700">
        <div className="max-w-7xl mx-auto flex justify-between items-center">
          <h1 className="text-xl font-bold">Code Practice</h1>
          <button onClick={() => router.push("/problems")} className="px-3 py-1 bg-gray-700 rounded hover:bg-gray-600">
            Back to Problems
          </button>
        </div>
      </div>

      <div className="flex-1 flex flex-col md:flex-row">
        {/* Problem Description Panel */}
        <div className="md:w-1/2 p-4 overflow-y-auto border-r border-gray-700">
          <div className="max-w-2xl mx-auto">
            <h1 className="text-2xl font-bold mb-4">{problem.title}</h1>

            <div className="prose prose-invert max-w-none">
              <div className="mb-6">{problem.description}</div>

              <div className="mb-4">
                <p>
                  <strong>Category:</strong> {problem.category}
                </p>
                <p>
                  <strong>Input Format:</strong> {problem.inputFormat}
                </p>
                <p>
                  <strong>Output Format:</strong> {problem.outputFormat}
                </p>
                <p>
                  <strong>Constraints:</strong> {problem.constraints}
                </p>
              </div>

              <h4 className="font-semibold mt-6 mb-3">Test Cases:</h4>
              <div className="space-y-4">
                {problem.testCases.slice(0, 2).map((tc, i) => (
                  <div key={i} className="bg-[#252525] p-4 rounded-md">
                    <div className="mb-2">
                      <span className="font-mono font-bold">Input:</span>
                      <code className="ml-2 bg-[#333] px-2 py-1 rounded font-mono">{tc.input}</code>
                    </div>
                    <div>
                      <span className="font-mono font-bold">Output:</span>
                      <code className="ml-2 bg-[#333] px-2 py-1 rounded font-mono">{tc.output}</code>
                    </div>
                  </div>
                ))}
              </div>

              {problem.testCases.length > 2 && (
                <div className="mt-4 text-gray-400 italic">
                  {problem.testCases.length - 2} more test cases are hidden
                </div>
              )}
            </div>
          </div>
        </div>

        {/* Code Editor Panel */}
        <div className="md:w-1/2 flex flex-col">
          {/* Editor Controls */}
          <div className="bg-[#252525] p-3 border-b border-gray-700 flex items-center">
            <select
              value={language}
              onChange={(e) => handleLanguageChange(e.target.value)}
              className="bg-[#333] text-gray-200 px-3 py-1 rounded mr-4 border border-gray-600"
            >
              <option value="java">Java</option>
            </select>

            <button
              onClick={handleRunCode}
              disabled={isRunning}
              className="flex items-center px-4 py-1 bg-blue-600 text-white rounded mr-2 hover:bg-blue-700 disabled:opacity-50"
            >
              <Play className="w-4 h-4 mr-1" />
              {isRunning ? "Running..." : "Run Code"}
            </button>

            <button
              onClick={submitCode}
              disabled={isSubmitting || isRunning}
              className="flex items-center px-4 py-1 bg-green-600 text-white rounded hover:bg-green-700 disabled:opacity-50"
            >
              <Send className="w-4 h-4 mr-1" />
              {isSubmitting ? "Submitting..." : "Submit"}
            </button>
          </div>

          {/* Code Editor */}
          <div className="flex-1">
            <Editor
              height="100%"
              language={language}
              value={code}
              onChange={handleCodeChange}
              theme="vs-dark"
              options={{
                minimap: { enabled: false },
                fontSize: 14,
                scrollBeyondLastLine: false,
                automaticLayout: true,
                tabSize: 2,
              }}
            />
          </div>

          {/* Output/Results Panel */}
          <div className="h-1/3 bg-[#252525] overflow-y-auto border-t border-gray-700">
            <div className="p-4">
              {/* Test Execution Progress */}
              {isRunning && runningTestCase && (
                <div className="mb-4">
                  <h3 className="text-sm font-semibold mb-2 text-gray-400">
                    Running Test Case {runningTestCase.index + 1}/{problem.testCases.length}
                  </h3>
                  <div
                    className={`p-3 rounded border ${
                      runningTestCase.status === "running"
                        ? "bg-blue-900/20 border-blue-800"
                        : runningTestCase.status === "passed"
                          ? "bg-green-900/20 border-green-800"
                          : "bg-red-900/20 border-red-800"
                    }`}
                  >
                    <div className="flex items-center mb-2">
                      {runningTestCase.status === "running" ? (
                        <div className="animate-pulse text-blue-500 mr-2">●</div>
                      ) : runningTestCase.status === "passed" ? (
                        <CheckCircle className="text-green-500 w-5 h-5 mr-2" />
                      ) : (
                        <XCircle className="text-red-500 w-5 h-5 mr-2" />
                      )}
                      <span className="font-medium">
                        {runningTestCase.status === "running"
                          ? "Running..."
                          : runningTestCase.status === "passed"
                            ? "Passed!"
                            : "Failed!"}
                      </span>
                    </div>
                    <div className="text-sm mb-1">
                      <span className="font-mono">Input: </span>
                      <span className="font-mono text-gray-400">{runningTestCase.input}</span>
                    </div>
                    <div className="text-sm mb-1">
                      <span className="font-mono">Expected: </span>
                      <span className="font-mono text-gray-400">{runningTestCase.expected}</span>
                    </div>
                    {runningTestCase.output && (
                      <div className="text-sm">
                        <span className="font-mono">Output: </span>
                        <span className="font-mono text-gray-400">{runningTestCase.output}</span>
                      </div>
                    )}
                  </div>
                </div>
              )}

              {/* Completed Test Results */}
              {testResults.length > 0 && !isRunning && (
                <div>
                  <h3 className="text-sm font-semibold mb-2 text-gray-400">Test Results:</h3>
                  <div className="space-y-2">
                    {testResults.map((result, idx) => (
                      <div
                        key={idx}
                        className={`p-2 rounded flex items-start ${
                          result.passed
                            ? "bg-green-900/20 border border-green-800"
                            : "bg-red-900/20 border border-red-800"
                        }`}
                      >
                        <div className={`mr-2 font-bold ${result.passed ? "text-green-500" : "text-red-500"}`}>
                          {result.passed ? <CheckCircle className="w-4 h-4" /> : <XCircle className="w-4 h-4" />}
                        </div>
                        <div className="flex-1">
                          <div className="text-sm">
                            <span className="font-mono">Input: </span>
                            <span className="font-mono text-gray-400">{result.input}</span>
                          </div>
                          <div className="text-sm">
                            <span className="font-mono">Expected: </span>
                            <span className="font-mono text-gray-400">{result.expected}</span>
                          </div>
                          <div className="text-sm">
                            <span className="font-mono">Output: </span>
                            <span className="font-mono text-gray-400">{result.output}</span>
                          </div>
                        </div>
                      </div>
                    ))}
                  </div>

                  <div className="mt-4 text-center">
                    <span
                      className={`font-bold ${testResults.every((r) => r.passed) ? "text-green-500" : "text-red-500"}`}
                    >
                      {testResults.filter((r) => r.passed).length}/{testResults.length} tests passed
                    </span>
                  </div>
                </div>
              )}

              {/* Console Output */}
              {output && (
                <div className={testResults.length > 0 ? "mt-4" : ""}>
                  <h3 className="text-sm font-semibold mb-2 text-gray-400">Console Output:</h3>
                  <pre className="bg-[#1e1e1e] p-3 rounded font-mono text-sm whitespace-pre-wrap">{output}</pre>
                </div>
              )}

              {!isRunning && !output && testResults.length === 0 && (
                <div className="text-gray-500 italic">Run your code to see output here</div>
              )}
            </div>
          </div>
        </div>
      </div>
    </div>
  )
}
