import React, { useEffect, useRef, useState } from "react";
import { Client } from "@stomp/stompjs";
import { useNavigate, useLocation } from "react-router-dom";
import Navbar2 from "./Navbar2";

const SOCKET_URL = "ws://localhost:8788/ws";

const WaitingRoom = () => {
  const { search } = useLocation();
  const queryParams = new URLSearchParams(search);
  const roomCode = queryParams.get("roomCode");
  const username = queryParams.get("username") || localStorage.getItem("username");

  const [players, setPlayers] = useState([]);
  const [host, setHost] = useState("");
  const [roomStatus, setRoomStatus] = useState("waiting");
  const [timeLeft, setTimeLeft] = useState(120);
  const [error, setError] = useState("");
  const stompClientRef = useRef(null);
  const navigate = useNavigate();

  useEffect(() => {
    if (!roomCode || !username) {
      setError("Room code and username are required");
      return;
    }

    const client = new Client({
      brokerURL: SOCKET_URL,
      reconnectDelay: 5000,
      onConnect: () => {
        console.log("‚úÖ Connected to WebSocket");

        // Subscribe to room updates
        client.subscribe(`/topic/room/${roomCode}`, (message) => {
          try {
            console.log("üîµ Received message:", message.body);
            const roomData = JSON.parse(message.body);

            if (roomData.players) {
              if (roomData.players["@class"] === "java.util.LinkedHashSet") {
                setPlayers(roomData.players[1] || []);
              } else if (Array.isArray(roomData.players)) {
                setPlayers(roomData.players);
              }
            }

            setHost(roomData.host || "");
            setRoomStatus(roomData.status || "waiting");
            console.log("‚úÖ Updated room state:", roomData);
          } catch (error) {
            console.error("‚ùå Error parsing message:", error);
            setError("Error updating room state");
          }
        });

        // Join the room
        client.publish({
          destination: "/app/room/join",
          body: JSON.stringify({ roomCode, username }),
        });
        console.log("üì§ Sent join request for", username, "to room", roomCode);
      },
      onStompError: (frame) => {
        console.error("‚ùå WebSocket error:", frame);
        setError("Failed to connect to game server");
      }
    });

    client.activate();
    stompClientRef.current = client;

    return () => {
      if (stompClientRef.current && stompClientRef.current.connected) {
        // Send leave message before disconnecting
        stompClientRef.current.publish({
          destination: "/app/room/leave",
          body: JSON.stringify({ roomCode, username })
        });
        console.log("üëã Sent leave message for", username);
        stompClientRef.current.deactivate();
      }
    };
  }, [roomCode, username]);

  useEffect(() => {
    const handleUnload = () => {
      stompClientRef.current?.publish({
        destination: "/app/room/leave",
        body: JSON.stringify({ roomCode, username }),
      });
    };
    window.addEventListener("beforeunload", handleUnload);
    return () => window.removeEventListener("beforeunload", handleUnload);
  }, [roomCode, username]);

  useEffect(() => {
    const timer = setInterval(() => {
      setTimeLeft((prev) => {
        if (prev === 1) {
          stompClientRef.current?.publish({
            destination: "/app/room/end",
            body: JSON.stringify({ roomCode }),
          });
          navigate("/home");
        }
        return prev - 1;
      });
    }, 1000);

    return () => clearInterval(timer);
  }, [roomCode, navigate]);

  useEffect(() => {
    if (roomStatus === "started") {
      const audio = new Audio("/sounds/battle-start.mp3");
      audio.play().catch(() => {});
      if ("vibrate" in navigator) {
        navigator.vibrate(200);
      }
      navigate(`/battle/${roomCode}`);
    }
  }, [roomStatus, roomCode, navigate]);

  const handleStart = () => {
    if (stompClientRef.current?.connected) {
      stompClientRef.current.publish({
        destination: "/app/room/start",
        body: JSON.stringify({ roomCode })
      });
      console.log("‚öîÔ∏è Starting battle in room", roomCode);
    }
  };

  const handleExit = () => {
    if (stompClientRef.current?.connected) {
      stompClientRef.current.publish({
        destination: "/app/room/leave",
        body: JSON.stringify({ roomCode, username })
      });
      console.log("üö™ Exiting room", roomCode);
    }
    navigate("/");
  };

  return (
    <div className="min-h-screen bg-gradient-to-r from-purple-100 to-indigo-100 flex flex-col justify-center items-center px-6">
      <div className="bg-white shadow-2xl rounded-2xl p-10 w-full max-w-3xl relative overflow-hidden">
        {/* Background Overlay */}
        <div className="absolute inset-0 bg-gradient-to-br from-indigo-50 to-purple-50 opacity-50"></div>

        {/* Main Content */}
        <div className="relative z-10">
          <h2 className="text-3xl font-extrabold text-center mb-6 text-transparent bg-clip-text bg-gradient-to-r from-indigo-600 to-purple-600">
            ‚öîÔ∏è CodeClash Battle Room
          </h2>

          <div className="bg-white/80 backdrop-blur-sm rounded-xl p-4 mb-6 shadow-lg border border-indigo-100">
            <p className="text-center text-gray-600">
              Room Code:
              <span className="font-bold text-indigo-600 text-lg">{roomCode}</span>
            </p>
          </div>

          {/* Status Message */}
          <div
            className={`text-center mb-6 p-3 rounded-xl ${
              username === host ? "bg-indigo-50" : "bg-purple-50"
            }`}
          >
            {username === host ? (
              <p className="text-indigo-600 font-semibold flex items-center justify-center gap-2">
                <span className="text-xl">üßô‚Äç‚ôÇÔ∏è</span>
                You are the Battle Master
              </p>
            ) : (
              <p className="text-purple-600 font-semibold flex items-center justify-center gap-2">
                <span className="text-xl">‚öîÔ∏è</span>
                Ready for battle! Waiting for the Battle Master...
              </p>
            )}
          </div>

          {/* Players List */}
          <div className="bg-gradient-to-br from-gray-50 to-indigo-50 rounded-xl p-6 mb-6 shadow-lg border border-indigo-100">
            <h3 className="text-lg font-semibold text-indigo-700 mb-4 flex items-center gap-2">
              <span>üë•</span> Warriors ({players.length})
            </h3>
            <ul className="grid grid-cols-2 gap-3">
              {players.map((player, index) => (
                <li
                  key={index}
                  className={`
                    py-3 px-4 rounded-lg font-medium flex items-center gap-2
                    ${
                      player === host
                        ? "bg-gradient-to-r from-indigo-100 to-purple-100 text-indigo-800 font-bold shadow-sm"
                        : "bg-white text-gray-700"
                    }
                    ${player === username ? "ring-2 ring-indigo-200" : ""}
                    transform hover:scale-105 transition-all duration-200
                  `}
                >
                  <span className="w-2 h-2 rounded-full bg-indigo-400 animate-pulse"></span>
                  {player === username ? (
                    <span className="text-transparent bg-clip-text bg-gradient-to-r from-indigo-600 to-purple-600">
                      {player} (You)
                    </span>
                  ) : (
                    player
                  )}
                  {player === host && (
                    <span className="text-xs px-2 py-1 bg-indigo-100 text-indigo-600 rounded-full ml-auto">
                      Battle Master
                    </span>
                  )}
                </li>
              ))}
            </ul>
          </div>

          {/* Countdown Timer */}
          <div className="text-center mb-6">
            <div
              className={`inline-block px-6 py-3 rounded-full ${
                timeLeft <= 30
                  ? "bg-red-50 text-red-600 animate-pulse"
                  : "bg-indigo-50 text-indigo-600"
              }`}
            >
              <span className="font-semibold flex items-center gap-2">
                <span className="text-xl">‚è≥</span>
                Time left: {Math.floor(timeLeft / 60)}:
                {(timeLeft % 60).toString().padStart(2, "0")}
              </span>
            </div>
          </div>

          {/* Action Buttons */}
          <div className="space-y-4">
            {/* Start Button or Waiting Message */}
            {roomStatus === "waiting" && (
              <div className="text-center">
                {username === host ? (
                  <button
                    onClick={handleStart}
                    className="
                      bg-gradient-to-r from-indigo-600 to-purple-600 text-white
                      px-8 py-3 rounded-xl font-semibold
                      hover:shadow-lg hover:scale-105 transition-all duration-200
                      flex items-center gap-2 mx-auto
                    "
                  >
                    <span className="text-xl">‚öîÔ∏è</span>
                    Begin Battle
                  </button>
                ) : (
                  <p className="text-purple-600 italic text-sm">
                    Waiting for the Battle Master to start the challenge...
                  </p>
                )}
              </div>
            )}

            {/* Exit Button - Always visible */}
            <div className="text-center">
              <button
                onClick={handleExit}
                className="
                  bg-gradient-to-r from-red-500 to-red-600 text-white
