import { useLocation, useNavigate } from "react-router-dom";
import { toast } from "react-toastify";
import Navbar2 from "./Navbar2";
import Editor from "@monaco-editor/react";
import problems from "../data/problems.json";

export default function PracticePage() {
  const location = useLocation()
  const navigate = useNavigate()
  const [problem, setProblem] = useState(null)
  const [code, setCode] = useState("")
  const [language, setLanguage] = useState("java")
  const [output, setOutput] = useState("")
  const [isRunning, setIsRunning] = useState(false)
  const [isSubmitting, setIsSubmitting] = useState(false)
  const [testResults, setTestResults] = useState([])
  const [testCaseIndex, setTestCaseIndex] = useState(0)
  const [singleTestResult, setSingleTestResult] = useState(null)
  const [customInput, setCustomInput] = useState("")
  const [customResult, setCustomResult] = useState(null)
  const [testCaseInput, setTestCaseInput] = useState("")
  const input = problem?.testCases.map(tc => tc.input) || ""

  // Default starter code templates for different languages
  const starterCodeTemplates = {
    java: `class Solution {
}`
  }

  // Add this mapping at the top of your file or before the runCode function
const languageIdMapping = {
  java: 62,     
};

  useEffect(() => {
    // Get problem from location state or redirect back to problem list
    const problemId = location.state?.problemId;
    if (!problemId) {
      navigate('/problems');
      return;
    }

    // Find the problem in problems.json
    const selectedProblem = problems.find(p => p.title === problemId);
    if (selectedProblem) {
      setProblem(selectedProblem);
      // Set initial starter code based on selected language
      setCode(starterCodeTemplates[language] || '');
    } else {
      toast.error('Problem not found');
      navigate('/problems');
    }
  }, [location, navigate, language])

  // Change language and update starter code
  const handleLanguageChange = (newLanguage) => {
    setLanguage(newLanguage)
    setCode(starterCodeTemplates[newLanguage])
  }

  // Mock function to run code (in a real app, this would send code to a backend)
  const runCode = async () => {
    console.log(code);
    console.log(input);
    setOutput("")
    try {
      const token = sessionStorage.getItem("Authtoken")
      // console.log(token);
      if (!token) throw new Error("Unauthorized")
      const response = await fetch("http://localhost:8788/api/coderun", {
        method: "POST",
        headers: {
          "Content-Type": "application/json",
          "Authorization": `Bearer ${token}`,
        },
        body: JSON.stringify({
          code,
          language,
          testCaseInput: testCaseInput
        }),
      });
      const result = await response.json();
      setOutput(result.output);
    } catch (error) {
      toast.error('Error running code');
      console.error(error);
    } finally {
      setIsRunning(false);
    }
  };


  const handleRunSingleTestCase = async () => {
  if (!problem || !problem.testCases || problem.testCases.length === 0) return;
  setIsRunning(true);
  setSingleTestResult(null);
  const token = sessionStorage.getItem("Authtoken");
  if (!token) {
    toast.error("Unauthorized");
    setIsRunning(false);
    return;
  }
  const testCase = problem.testCases[testCaseIndex];
  try {
    const response = await fetch("http://localhost:8788/api/coderun", {
      method: "POST",
      headers: {
        "Content-Type": "application/json",
        "Authorization": `Bearer ${token}`,
      },
      body: JSON.stringify({
        code,
        language,
        testCaseInput: testCase.input
      }),
    });
    const result = await response.json();
    const isPassed = result.output.trim() === testCase.expectedOutput.trim();
    setSingleTestResult({
      input: testCase.input,
      expected: testCase.expectedOutput,
      output: result.output.trim(),
      passed: isPassed,
    });
  } catch (error) {
    setSingleTestResult({
      input: testCase.input,
      expected: testCase.expectedOutput,
      output: "Error",
      passed: false,
    });
    toast.error('Error running test case');
  } finally {
    setIsRunning(false);
  }
};

const handleRunNextTestCase = () => {
  if (problem && testCaseIndex < problem.testCases.length - 1) {
    setTestCaseIndex(testCaseIndex + 1);
    setSingleTestResult(null);
  }
};

const handleRunCustomTestCase = async () => {
  setIsRunning(true);
  setCustomResult(null);
  const token = sessionStorage.getItem("Authtoken");
  if (!token) {
    toast.error("Unauthorized");
    setIsRunning(false);
    return;
  }
  try {
    const response = await fetch("http://localhost:8788/api/coderun", {
      method: "POST",
      headers: {
        "Content-Type": "application/json",
        "Authorization": `Bearer ${token}`,
      },
      body: JSON.stringify({
        code,
        language,
        testCaseInput: customInput
      }),
    });
    const result = await response.json();
    setCustomResult(result.output.trim());
  } catch (error) {
    setCustomResult("Error");
    toast.error('Error running custom test case');
  } finally {
    setIsRunning(false);
  }
};




  const handleCodeChange = (newCode)=>{
    setCode(newCode)
  }

  // Mock function to submit and test code
  const submitCode = async () => {
    if (!problem) {
      toast.error('No problem selected');
      return;
    }

    setIsSubmitting(true);

    try {
      const token = localStorage.getItem('token');
      const response = await fetch(`${process.env.REACT_APP_BACKEND_URL}/submissions`, {
        method: 'POST',
        headers: {
          "Content-Type": "application/json",
          "Authorization": `Bearer ${token}`
        },
        body: JSON.stringify({
          problemId: problem.id,
          code,
          language
        })
      });

      const result = await response.json();

      if (result.success) {
        toast.success('Code submitted successfully!');
        // Additional logic for successful submission
      } else {
        toast.error(result.message || 'Submission failed');
      }
    } catch (error) {
      toast.error('Error submitting code');
      console.error(error);
    } finally {
      setIsSubmitting(false);
    }
  };

  const testCode = () => {
    setIsSubmitting(true)
    setTestResults([])

    // Simulate code testing delay
    setTimeout(() => {
      // Mock test results
      const results = problem.testCases.map((testCase, index) => {
        // In a real app, you would actually run the code against the test cases
        const passed = Math.random() > 0.3 // Random pass/fail for demo
        return {
          testCase,
          passed,
          output: passed ? testCase.output : "Wrong output",
        }
      })

      setTestResults(results)
      setIsSubmitting(false)
    }, 1500)
  }

  if (!problem) {
    return <div>Loading...</div>
  }



  return (
    <div className="min-h-screen bg-[#1e1e1e] text-gray-200 flex flex-col">
      <Navbar2 />

      <div className="flex-1 flex flex-col md:flex-row">
        {/* Problem Description Panel */}
        <div className="md:w-1/2 p-4 overflow-y-auto border-r border-gray-700">
          <div className="max-w-2xl mx-auto">
            <h1 className="text-2xl font-bold mb-4">{problem.title}</h1>

            <div className="prose prose-invert max-w-none">
              <div className="mb-6" dangerouslySetInnerHTML={{ __html: problem.description }} />

              <div className="mb-4">
                <p>
                  <strong>Category:</strong> {problem.category}
                </p>
                <p>
                  <strong>Input Format:</strong> {problem.inputFormat}
                </p>
                <p>
                  <strong>Output Format:</strong> {problem.outputFormat}
                </p>
                <p>
                  <strong>Constraints:</strong> {problem.constraints}
                </p>
              </div>

              <h4 className="font-semibold mt-6 mb-3">Test Cases:</h4>
              <div className="space-y-4">
                {problem.testCases.slice(0, 2).map((tc, i) => (
                  <div key={i} className="bg-[#252525] p-4 rounded-md">
                    <div className="mb-2">
                      <span className="font-mono font-bold">Input:</span>
                      <code className="ml-2 bg-[#333] px-2 py-1 rounded font-mono">{tc.input}</code>
                    </div>
                    <div>
                      <span className="font-mono font-bold">Output:</span>
                      <code className="ml-2 bg-[#333] px-2 py-1 rounded font-mono">{tc.output}</code>
                    </div>
                  </div>
                ))}
              </div>

              {problem.testCases.length > 2 && (
                <div className="mt-4 text-gray-400 italic">
                  {problem.testCases.length - 2} more test cases are hidden
                </div>
              )}
            </div>
          </div>
        </div>

        {/* Code Editor Panel */}
        <div className="md:w-1/2 flex flex-col">
          {/* Editor Controls */}
          <div className="bg-[#252525] p-3 border-b border-gray-700 flex items-center">
            <select
              value={language}
              onChange={(e) => handleLanguageChange(e.target.value)}
              className="bg-[#333] text-gray-200 px-3 py-1 rounded mr-4 border border-gray-600"
            >
              <option value="java">Java</option>
            </select>

            {/* Removed old Run Code button. Now handled in output panel per test case. */}

            <button
              onClick={submitCode}
              disabled={isSubmitting}
              className="px-4 py-1 bg-green-600 text-white rounded hover:bg-green-700 disabled:opacity-50"
            >
              {isSubmitting ? "Submitting..." : "Submit"}
            </button>
          </div>

          {/* Code Editor */}
          <div className="flex-1">
            <Editor
              height="100%"
              language={language}
              value={code}
              onChange={handleCodeChange}

              theme="vs-dark"
              options={{
                minimap: { enabled: false },
                fontSize: 14,
                scrollBeyondLastLine: false,
                automaticLayout: true,
                tabSize: 2,
              }}
            />
          </div>

          {/* Output/Results Panel */}
          <div className="h-1/3 bg-[#252525] overflow-y-auto border-t border-gray-700">
            <div className="p-4">
              {/* Console Output Tab */}
              {output && (
                <div>
                  <h3 className="text-sm font-semibold mb-2 text-gray-400">Console Output:</h3>
                  <pre className="bg-[#1e1e1e] p-3 rounded font-mono text-sm whitespace-pre-wrap">{output}</pre>
                </div>
              )}

              {/* Single Test Case Result */}
              {problem && problem.testCases && problem.testCases.length > 0 && (
                <div className="mt-4">
                  <h3 className="text-sm font-semibold mb-2 text-gray-400">Test Case {testCaseIndex + 1} of {problem.testCases.length}</h3>
                  <button
                    onClick={handleRunSingleTestCase}
                    disabled={isRunning}
                    className="px-4 py-1 bg-blue-600 text-white rounded mr-2 hover:bg-blue-700 disabled:opacity-50"
                  >
                    {isRunning ? "Running..." : "Run Test Case"}
                  </button>
                  <button
                    onClick={handleRunNextTestCase}
                    disabled={testCaseIndex >= problem.testCases.length - 1}
                    className="px-4 py-1 bg-gray-600 text-white rounded hover:bg-gray-700 disabled:opacity-50"
                  >
                    Next Test Case
                  </button>
                  {singleTestResult && (
                    <div className={`mt-4 p-2 rounded flex items-start ${singleTestResult.passed ? "bg-green-900/20 border border-green-800" : "bg-red-900/20 border-red-800"}`}>
                      <div className={`mr-2 font-bold ${singleTestResult.passed ? "text-green-500" : "text-red-500"}`}>{singleTestResult.passed ? "✓" : "✗"}</div>
                      <div className="flex-1">
                        <div className="text-sm"><span className="font-mono">Input: </span><span className="font-mono text-gray-400">{singleTestResult.input}</span></div>
                        <div className="text-sm"><span className="font-mono">Expected: </span><span className="font-mono text-gray-400">{singleTestResult.expected}</span></div>
                        <div className="text-sm"><span className="font-mono">Output: </span><span className="font-mono text-gray-400">{singleTestResult.output}</span></div>
                      </div>
                    </div>
                  )}
                </div>
              )}

              {/* Custom Test Case */}
              <div className="mt-6">
                <h3 className="text-sm font-semibold mb-2 text-gray-400">Custom Test Case</h3>
                <div className="flex items-center mb-2">
                  <input
                    type="text"
                    className="flex-1 px-2 py-1 rounded bg-[#1e1e1e] text-white border border-gray-700 mr-2"
                    placeholder="Enter custom input"
                    value={customInput}
                    onChange={e => setCustomInput(e.target.value)}
                  />
                  <button
                    onClick={handleRunCustomTestCase}
                    disabled={isRunning || !customInput}
                    className="px-4 py-1 bg-purple-600 text-white rounded hover:bg-purple-700 disabled:opacity-50"
                  >
                    {isRunning ? "Running..." : "Run Custom Test"}
                  </button>
                </div>
                {customResult !== null && (
                  <div className="mt-2 bg-[#1e1e1e] p-2 rounded text-white">
                    <span className="font-mono">Output: </span>
                    <span className="font-mono text-gray-400">{customResult}</span>
                  </div>
                )}
              </div>

              {!output && !singleTestResult && customResult === null && (
                <div className="text-gray-500 italic">Run your code to see output here</div>
              )}
            </div>
          </div>
        </div>
      </div>
    </div>
  );
}
