import React, { useState, useEffect } from 'react';
import Navbar2 from './Navbar2';
import problems from '../data/problems.json';
import Editor from '@monaco-editor/react';
import { toast } from 'react-toastify';

const CodingPage = () => {
  const [selectedProblem, setSelectedProblem] = useState(null);
  const [code, setCode] = useState('');
  const [language, setLanguage] = useState('java');
  const [isRunning, setIsRunning] = useState(false);
  const [testResults, setTestResults] = useState([]);

  // JDoodle API credentials (consider moving to environment variables)
  const JDOODLE_CLIENT_ID = 'YOUR_CLIENT_ID';
  const JDOODLE_CLIENT_SECRET = 'YOUR_CLIENT_SECRET';

  const executeCodeOnJDoodle = async (script, stdin) => {
    try {
      const response = await fetch('https://api.jdoodle.com/v1/execute', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json'
        },
        body: JSON.stringify({
          clientId: JDOODLE_CLIENT_ID,
          clientSecret: JDOODLE_CLIENT_SECRET,
          script: script,
          stdin: stdin,
          language: language
        })
      });

      if (!response.ok) {
        throw new Error('Code execution failed');
      }

      const result = await response.json();
      return result;
    } catch (error) {
      console.error('JDoodle API Error:', error);
      toast.error('Failed to execute code');
      return null;
    }
  };

  const runCode = async () => {
    if (!selectedProblem) {
      toast.error('Please select a problem');
      return;
    }

    setIsRunning(true);
    setTestResults([]);

    try {
      // Prepare test results for each test case
      const results = await Promise.all(
        selectedProblem.testCases.map(async (testCase) => {
          const script = language === 'java' 
            ? `public class Solution { 
                public static void main(String[] args) {
                  ${code}
                }
              }`
            : code;

          const executionResult = await executeCodeOnJDoodle(script, testCase.input);

          if (!executionResult) {
            return {
              testCase,
              passed: false,
              output: 'Execution failed'
            };
          }

          const passed = executionResult.output.trim() === testCase.output.trim();

          return {
            testCase,
            passed,
            output: executionResult.output.trim(),
            error: executionResult.error
          };
        })
      );

      setTestResults(results);

      // Show overall result toast
      const allPassed = results.every(r => r.passed);
      if (allPassed) {
        toast.success('All test cases passed!');
      } else {
        toast.error('Some test cases failed');
      }
    } catch (error) {
      toast.error('Error running code');
      console.error(error);
    } finally {
      setIsRunning(false);
    }
  };

  const handleProblemSelect = (problem) => {
    setSelectedProblem(problem);
    // Reset code and test results when a new problem is selected
    setCode('');
    setTestResults([]);
  };

  return (
    <div className="min-h-screen bg-[#1e1e1e] text-gray-200 flex flex-col">
      <Navbar2 />

      <div className="container mx-auto px-4 py-8 flex flex-1">
        {/* Problem List */}
        <div className="w-1/4 pr-4 overflow-y-auto">
          <h2 className="text-xl font-bold mb-4">Problems</h2>
          {problems.map((problem) => (
            <div 
              key={problem.name}
              onClick={() => handleProblemSelect(problem)}
              className={`p-3 mb-2 rounded cursor-pointer ${
                selectedProblem?.name === problem.name 
                  ? 'bg-blue-600 text-white' 
                  : 'bg-[#252525] hover:bg-[#333]'
              }`}
            >
              {problem.title}
            </div>
          ))}
        </div>

        {/* Problem Details and Code Editor */}
        <div className="w-3/4 flex flex-col">
          {selectedProblem ? (
            <>
              {/* Problem Description */}
              <div className="bg-[#252525] p-6 rounded-lg mb-4">
                <h3 className="text-2xl font-bold mb-4">{selectedProblem.title}</h3>
                <p className="mb-4">{selectedProblem.description}</p>

                <div className="mb-2">
                  <strong>Input Format:</strong> {selectedProblem.inputFormat}
                </div>
                <div className="mb-2">
                  <strong>Output Format:</strong> {selectedProblem.outputFormat}
                </div>
                <div>
                  <strong>Constraints:</strong> {selectedProblem.constraints}
                </div>
              </div>

              {/* Code Editor */}
              <div className="flex-1 mb-4">
                <Editor
                  height="400px"
                  language={language}
                  value={code}
                  onChange={(newCode) => setCode(newCode || '')}
                  theme="vs-dark"
                  options={{
                    minimap: { enabled: false },
                    fontSize: 14,
                    scrollBeyondLastLine: false,
                    automaticLayout: true,
                    tabSize: 2,
                  }}
                />
              </div>

              {/* Controls */}
              <div className="flex space-x-4 mb-4">
                <select
                  value={language}
                  onChange={(e) => setLanguage(e.target.value)}
                  className="bg-[#333] text-gray-200 px-3 py-2 rounded"
                >
                  <option value="java">Java</option>
                  <option value="python3">Python</option>
                  <option value="cpp">C++</option>
                </select>

                <button
                  onClick={runCode}
                  disabled={isRunning}
                  className="px-4 py-2 bg-blue-600 text-white rounded hover:bg-blue-700 disabled:opacity-50"
                >
                  {isRunning ? 'Running...' : 'Run Code'}
                </button>
              </div>

              {/* Test Results */}
              {testResults.length > 0 && (
                <div className="bg-[#252525] p-4 rounded-lg">
                  <h4 className="text-lg font-semibold mb-4">Test Results</h4>
                  {testResults.map((result, index) => (
                    <div 
                      key={index} 
                      className={`p-3 mb-2 rounded ${
                        result.passed 
                          ? 'bg-green-900/20 border border-green-800' 
                          : 'bg-red-900/20 border border-red-800'
                      }`}
                    >
                      <div className="flex items-center mb-2">
                        <span className={`mr-2 font-bold ${
                          result.passed ? 'text-green-500' : 'text-red-500'
                        }`}>
                          {result.passed ? '✓' : '✗'}
                        </span>
                        <span>Test Case {index + 1}</span>
                      </div>
                      <div className="mb-1">
                        <strong>Input:</strong> {result.testCase.input}
                      </div>
                      <div className="mb-1">
                        <strong>Expected Output:</strong> {result.testCase.output}
                      </div>
                      <div>
                        <strong>Actual Output:</strong> {result.output}
                      </div>
                      {result.error && (
                        <div className="text-red-500 mt-2">
                          <strong>Error:</strong> {result.error}
                        </div>
                      )}
                    </div>
                  ))}
                </div>
              )}
            </>
          ) : (
            <div className="text-center text-gray-500">
              Select a problem to start coding
            </div>
          )}
        </div>
      </div>
    </div>
  );
};

export default CodingPage;
