import React, { useState, useEffect, useRef } from 'react';
import Navbar2 from './Navbar2';
import problems from '../data/problems.json';
import { toast } from 'react-toastify';
// Removed next/script import

const CodingPage = () => {
  const [selectedProblem, setSelectedProblem] = useState(null);
  const [code, setCode] = useState(`import java.util.*;
public class Solution {
    public static void main(String[] args) {
        // Your code here
    }
}`);
  const [language, setLanguage] = useState('java');
  const [isRunning, setIsRunning] = useState(false);
  const [testResults, setTestResults] = useState([]);
  const pymRef = useRef(null);
  const embedRef = useRef(null);

  // Predefined JDoodle embed URLs for different languages
  const JDOODLE_EMBEDS = {
    java: 'https://www.jdoodle.com/embed/v1/c034f1e95725122e',
    python: 'https://www.jdoodle.com/embed/v1/python3',
    cpp: 'https://www.jdoodle.com/embed/v1/cpp'
  };

  useEffect(() => {
    // Check if Pym.js is already loaded
    if (window.pym) {
      try {
        pymRef.current = new window.pym.Parent('jdoodle-embed', JDOODLE_EMBEDS[language], {});
      } catch (error) {
        toast.error('Failed to initialize code editor');
        console.error('Pym.js initialization error:', error);
      }
      return;
    }

    // Load Pym.js script dynamically
    const script = document.createElement('script');
    script.src = 'https://www.jdoodle.com/assets/jdoodle-pym.min.js';
    script.async = true;
    script.onerror = () => {
      toast.error('Failed to load code editor script');
      console.error('Script loading error');
    };

    script.onload = () => {
      try {
        if (window.pym) {
          pymRef.current = new window.pym.Parent('jdoodle-embed', JDOODLE_EMBEDS[language], {});
        } else {
          throw new Error('Pym.js not available');
        }
      } catch (error) {
        toast.error('Failed to initialize code editor');
        console.error('Pym.js initialization error:', error);
      }
    };

    document.body.appendChild(script);

    // Cleanup function
    return () => {
      if (script.parentNode) {
        document.body.removeChild(script);
      }
    };
  }, [language]);

  // Listen for code changes from iframe
  useEffect(() => {
    const handleMessage = (event) => {
      if (event.data.type === 'code-change') {
        setCode(event.data.code);
      }
    };

    window.addEventListener('message', handleMessage);
    return () => {
      window.removeEventListener('message', handleMessage);
    };
  }, []);

  const executeCodeOnJDoodle = async () => {
    if (!pymRef.current) {
      toast.error('Code editor not loaded');
      return;
    }

    try {
      // Trigger code execution through Pym.js
      pymRef.current.sendMessage('run');

      // Note: Actual output handling would require additional Pym.js communication
      toast.success('Code execution triggered');
      return true;
    } catch (error) {
      toast.error('Failed to execute code');
      console.error(error);
      return false;
    }
  };

  const runCode = async () => {
    if (!selectedProblem) {
      toast.error('Please select a problem', { position: 'top-right' });
      return;
    }

    setIsRunning(true);
    setTestResults([]);

    try {
      // Trigger code execution
      const executionResult = await executeCodeOnJDoodle();

      // Simulate test case processing (actual implementation would depend on Pym.js communication)
      const simulatedResults = selectedProblem.testCases.slice(0, 2).map(testCase => ({
        testCase,
        passed: executionResult && Math.random() > 0.5,
        output: 'Execution output would be here'
      }));

      setTestResults(simulatedResults);

      // Show overall result toast
      const allPassed = simulatedResults.every((r) => r.passed);
      if (allPassed) {
        toast.success('All test cases passed!', { position: 'top-right' });
      } else {
        toast.error('Some test cases failed', { position: 'top-right' });
      }
    } catch (error) {
      toast.error('Error running code', { position: 'top-right' });
    } finally {
      setIsRunning(false);
    }
  };

  const handleProblemSelect = (problem) => {
    setSelectedProblem(problem);
    // Reset code and test results when a new problem is selected
    setCode('');
    setTestResults([]);
  };

  useEffect(() => {
    const handleMessage = (event) => {
      if (event.data.type === 'code-change') {
        setCode(event.data.code);
      }
    };

    window.addEventListener('message', handleMessage);
    return () => {
      window.removeEventListener('message', handleMessage);
    };
  }, []);

  return (
    <div className="min-h-screen bg-[#1e1e1e] text-gray-200 flex flex-col">
      <Navbar2 />

      <div className="container mx-auto px-4 py-8 flex flex-1">
        {/* Problem List */}
        <div className="w-1/4 pr-4 overflow-y-auto">
          <h2 className="text-xl font-bold mb-4">Problems</h2>
          {problems.map((problem) => (
            <div 
              key={problem.name}
              onClick={() => handleProblemSelect(problem)}
              className={`p-3 mb-2 rounded cursor-pointer ${
                selectedProblem?.name === problem.name 
                  ? 'bg-blue-600 text-white' 
                  : 'bg-[#252525] hover:bg-[#333]'
              }`}
            >
              {problem.title}
            </div>
          ))}
        </div>

        {/* Problem Details and Code Editor */}
        <div className="w-3/4 flex flex-col">
          {selectedProblem ? (
            <>
              {/* Problem Description */}
              <div className="bg-[#252525] p-6 rounded-lg mb-4">
                <h3 className="text-2xl font-bold mb-4">{selectedProblem.title}</h3>
                <p className="mb-4">{selectedProblem.description}</p>

                <div className="mb-2">
                  <strong>Input Format:</strong> {selectedProblem.inputFormat}
                </div>
                <div className="mb-2">
                  <strong>Output Format:</strong> {selectedProblem.outputFormat}
                </div>
                <div>
                  <strong>Constraints:</strong> {selectedProblem.constraints}
                </div>
              </div>

              {/* Code Editor */}
              <div id="jdoodle-embed" ref={embedRef} className="flex-1 mb-4" style={{ width: '100%', height: '400px' }}></div>

              {/* Controls */}
              <div className="flex space-x-4 mb-4">
                <select
                  value={language}
                  onChange={(e) => setLanguage(e.target.value)}
                  className="bg-[#333] text-gray-200 px-3 py-2 rounded"
                >
                  <option value="java">Java</option>
                  <option value="python3">Python</option>
                  <option value="cpp">C++</option>
                </select>

                <button
                  onClick={runCode}
                  disabled={isRunning}
                  className="px-4 py-2 bg-blue-600 text-white rounded hover:bg-blue-700 disabled:opacity-50"
                >
                  {isRunning ? 'Running...' : 'Run Code'}
                </button>
              </div>

              {/* Test Results */}
              {testResults.length > 0 && (
                <div className="bg-[#252525] p-4 rounded-lg">
                  <h4 className="text-lg font-semibold mb-4">Test Results</h4>
                  {testResults.map((result, index) => (
                    <div 
                      key={index} 
                      className={`p-3 mb-2 rounded ${
                        result.passed 
                          ? 'bg-green-900/20 border border-green-800' 
                          : 'bg-red-900/20 border border-red-800'
                      }`}
                    >
                      <div className="flex items-center mb-2">
                        <span className={`mr-2 font-bold ${
                          result.passed ? 'text-green-500' : 'text-red-500'
                        }`}>
                          {result.passed ? '✓' : '✗'}
                        </span>
                        <span>Test Case {index + 1}</span>
                      </div>
                      <div className="mb-1">
                        <strong>Input:</strong> {result.testCase.input}
                      </div>
                      <div className="mb-1">
                        <strong>Expected Output:</strong> {result.testCase.output}
                      </div>
                      <div>
                        <strong>Actual Output:</strong> {result.output}
                      </div>
                      {result.error && (
                        <div className="text-red-500 mt-2">
                          <strong>Error:</strong> {result.error}
                        </div>
                      )}
                    </div>
                  ))}
                </div>
              )}
            </>
          ) : (
            <div className="text-center text-gray-500">
              Select a problem to start coding
            </div>
          )}
        </div>
      </div>
    </div>
  );
};

export default CodingPage;
