import { useState } from "react";
import { useNavigate } from "react-router-dom";

const CreateRoom = () => {
  const [difficulty, setDifficulty] = useState("easy");
  const [topic, setTopic] = useState("");
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);
  const navigate = useNavigate();

  const createRoom = async () => {
    setLoading(true);
    setError(null);

    try {
      const res = await fetch("http://192.168.9.100:8080/api/room/create", {
        method: "POST",
        headers: {
          "Content-Type": "application/json",
        },
        body: JSON.stringify({ difficulty, topic, host: "Player1" }), // Change host dynamically
      });

      if (!res.ok) throw new Error("Failed to create room");

      const roomCode = await res.text();
      navigate(`/room/${roomCode}`); // Redirect to room page
    } catch (err) {
      setError(err.message);
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="relative min-h-screen flex items-center justify-center bg-gradient-to-r from-purple-500 to-indigo-600">
      {/* Floating Circles for Effect */}
      <div className="absolute top-10 left-10 w-20 h-20 bg-white opacity-20 rounded-full animate-pulse"></div>
      <div className="absolute bottom-16 right-16 w-24 h-24 bg-white opacity-25 rounded-full animate-bounce"></div>
      <div className="absolute top-1/2 left-1/3 w-16 h-16 bg-white opacity-15 rounded-full animate-spin"></div>

      <div className="bg-white p-6 rounded-lg shadow-lg w-96 relative z-10">
        <h2 className="text-xl font-semibold mb-4 text-center text-gray-800">Create a Room</h2>

        <label className="block mb-2 text-gray-700">Select Difficulty:</label>
        <select
          className="w-full p-2 border rounded mb-4"
          value={difficulty}
          onChange={(e) => setDifficulty(e.target.value)}
        >
          <option value="easy">Easy</option>
          <option value="medium">Medium</option>
          <option value="hard">Hard</option>
        </select>

        <label className="block mb-2 text-gray-700">Enter Topic:</label>
        <input
          type="text"
          className="w-full p-2 border rounded mb-4"
          value={topic}
          onChange={(e) => setTopic(e.target.value)}
          placeholder="e.g., Data Structures"
        />

        <button
          onClick={createRoom}
          className="w-full bg-blue-500 text-white py-2 rounded-lg hover:bg-blue-600 transition duration-200 transform hover:scale-105"
          disabled={loading}
        >
          {loading ? "Creating..." : "Create Room"}
        </button>

        {error && <p className="text-red-500 text-sm mt-2">{error}</p>}
      </div>
    </div>
  );
};

export default CreateRoom;
