import { useEffect, useState, useRef } from "react";
import { Client } from "@stomp/stompjs";
import { useParams, useNavigate } from "react-router-dom";
import styles from "../styling/waitingRoom.css";
import Navbar2 from "./Navbar2";

const SOCKET_URL = "ws://localhost:8788/ws";

const WaitingRoom = () => {
  const { roomCode } = useParams();
  const navigate = useNavigate();
  const [players, setPlayers] = useState([]);
  const [timeLeft, setTimeLeft] = useState(120);
  const [isLoading, setIsLoading] = useState(true);
  const stompClientRef = useRef(null);

  useEffect(() => {
    if (stompClientRef.current) return;
  
    stompClientRef.current = new Client({
      brokerURL: "ws://localhost:8788/ws", // Plain WebSocket (not SockJS)
      reconnectDelay: 5000,
      debug: (msg) => console.log("üêõ STOMP Debug:", msg), // Debug logs
      onConnect: () => {
        console.log("‚úÖ Connected to WebSocket");
  
        const topic = `/topic/room/${roomCode}`;
        console.log(`üì° Subscribing to: ${topic}`);
  
        stompClientRef.current.subscribe(topic, (message) => {
          console.log(`üîî Received WebSocket message:`, message.body);
  
          try {
            const updatedRoom = JSON.parse(message.body);
            console.log("‚úÖ Parsed WebSocket Data:", updatedRoom);
  
            setPlayers((prevPlayers) => {
              const newPlayers = new Set(prevPlayers);
              (updatedRoom.players || []).forEach((player) => newPlayers.add(player));
              if (updatedRoom.host) newPlayers.add(updatedRoom.host);
              return Array.from(newPlayers);
            });
  
            setIsLoading(false);
          } catch (error) {
            console.error("‚ùå Error parsing WebSocket message:", error);
          }
        });
      },
      onStompError: (frame) => console.error("‚ùå WebSocket error:", frame),
      onWebSocketClose: () => console.log("üî¥ WebSocket disconnected"),
    });

    console.log("üöÄ Sent test WebSocket message");
    
  
    stompClientRef.current.activate();
  
    return () => {
      if (stompClientRef.current) {
        stompClientRef.current.deactivate();
        stompClientRef.current = null;
      }
    };
  }, [roomCode]);
  

  useEffect(() => {
    if (timeLeft <= 0) {
      navigate("/");
      return;
    }
    const timer = setInterval(() => setTimeLeft((prev) => prev - 1), 1000);
    return () => clearInterval(timer);
  }, [timeLeft, navigate]);

  return (
    <div>
      <Navbar2 />
      <div className="waiting-room">
        <h2 className="title">Waiting For Players</h2>
        <p className="room-code">Room Code: <strong>{roomCode}</strong></p>

        <div className="countdown">
          ‚è≥ Test starts in: <span className="timer">{timeLeft}s</span>
        </div>

        {isLoading ? (
          <div className="loading-container">
            <div className="spinner"></div>
            <p className="loading-text">Waiting for players...</p>
          </div>
        ) : (
          <>
            <h3 className="players-title">Players:</h3>
            <ul className="players-list">
              {players.length > 0 ? (
                players.map((player, index) => (
                  <li key={index} className="player-item">{player}</li>
                ))
              ) : (
                <p>No players joined yet.</p>
              )}
            </ul>
          </>
        )}

        <button className="cancel-button" onClick={() => navigate("/")}>
          Exit Room
        </button>
      </div>
    </div>
  );
};

export default WaitingRoom;
