import { useState } from "react";

export default function CreateRoom() {
  const [difficulty, setDifficulty] = useState("easy");
  const [topic, setTopic] = useState("");
  const [roomCode, setRoomCode] = useState(null);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);

  const createRoom = async () => {
    setLoading(true);
    setError(null);

    try {
      const res = await fetch("http://localhost:8788/api/room/create", {
        method: "POST",
        headers: {
          "Content-Type": "application/json",
        },
        body: JSON.stringify({ difficulty, topic }),
      });

      const data = await res.json();
      if (!res.ok) throw new Error(data.message || "Failed to create room");

      setRoomCode(data.roomCode);
    } catch (err) {
      setError(err.message);
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="min-h-screen flex items-center justify-center bg-gray-100">
      <div className="bg-white p-6 rounded-lg shadow-lg w-96">
        <h2 className="text-xl font-semibold mb-4 text-center">Create a Room</h2>

        <label className="block mb-2 text-gray-700">Select Difficulty:</label>
        <select
          className="w-full p-2 border rounded mb-4"
          value={difficulty}
          onChange={(e) => setDifficulty(e.target.value)}
        >
          <option value="easy">Easy</option>
          <option value="medium">Medium</option>
          <option value="hard">Hard</option>
        </select>

        <label className="block mb-2 text-gray-700">Enter Topic:</label>
        <input
          type="text"
          className="w-full p-2 border rounded mb-4"
          value={topic}
          onChange={(e) => setTopic(e.target.value)}
          placeholder="e.g., Data Structures"
        />

        <button
          onClick={createRoom}
          className="w-full bg-blue-500 text-white py-2 rounded hover:bg-blue-600"
          disabled={loading}
        >
          {loading ? "Creating..." : "Create Room"}
        </button>

        {error && <p className="text-red-500 text-sm mt-2">{error}</p>}

        {roomCode && (
          <div className="mt-4 p-4 bg-green-100 text-green-700 rounded text-center">
            Room Created! Code: <strong>{roomCode}</strong>
          </div>
        )}
      </div>
    </div>
  );
}
