import React, { useEffect, useRef, useState } from "react";
import { Client } from "@stomp/stompjs";
import SockJS from "sockjs-client";
import { useNavigate, useParams } from "react-router-dom";

const WaitingRoom = () => {
  const { roomCode } = useParams();
  const [players, setPlayers] = useState([]);
  const [host, setHost] = useState("");
  const [roomStatus, setRoomStatus] = useState("waiting");
  const [timeLeft, setTimeLeft] = useState(120); // 2 minutes
  const stompClientRef = useRef(null);
  const navigate = useNavigate();
  const username = localStorage.getItem("username");

  useEffect(() => {
    const socket = new SockJS("http://localhost:8080/ws");
    const stompClient = new Client({ webSocketFactory: () => socket });

    stompClient.onConnect = () => {
      stompClient.subscribe(`/topic/room/${roomCode}`, (message) => {
        const roomData = JSON.parse(message.body);
        setPlayers(roomData.players);
        setHost(roomData.host);
        setRoomStatus(roomData.status);
      });

      stompClient.publish({
        destination: "/app/room/join",
        body: JSON.stringify({ roomCode, username }),
      });
    };

    stompClient.activate();
    stompClientRef.current = stompClient;

    return () => {
      if (stompClientRef.current) {
        stompClientRef.current.deactivate();
      }
    };
  }, [roomCode, username]);

  // Auto-leave on tab close or refresh
  useEffect(() => {
    const handleUnload = () => {
      stompClientRef.current?.publish({
        destination: "/app/room/leave",
        body: JSON.stringify({ roomCode, username }),
      });
    };
    window.addEventListener("beforeunload", handleUnload);
    return () => window.removeEventListener("beforeunload", handleUnload);
  }, [roomCode, username]);

  useEffect(() => {
    const timer = setInterval(() => {
      setTimeLeft((prev) => {
        if (prev === 1) {
          stompClientRef.current?.publish({
            destination: "/app/room/end",
            body: JSON.stringify({ roomCode }),
          });
          navigate("/home");
        }
        return prev - 1;
      });
    }, 1000);

    return () => clearInterval(timer);
  }, [roomCode, navigate]);

  // Auto redirect when game starts
  useEffect(() => {
    if (roomStatus === "started") {
      const audio = new Audio("/sounds/battle-start.mp3");
      audio.play().catch(() => {});
      if ("vibrate" in navigator) {
        navigator.vibrate(200);
      }
      navigate(`/battle/${roomCode}`);
    }
  }, [roomStatus, roomCode, navigate]);

  const handleStart = () => {
    stompClientRef.current?.publish({
      destination: "/app/room/start",
      body: JSON.stringify({ roomCode }),
    });
  };

  return (
    <div className="min-h-screen bg-gradient-to-r from-purple-100 to-indigo-100 flex flex-col justify-center items-center px-6">
      <div className="bg-white shadow-2xl rounded-2xl p-10 w-full max-w-3xl">
        <h2 className="text-3xl font-extrabold text-center mb-6 text-indigo-700">‚öîÔ∏è CodeClash Battle Room</h2>
        <p className="text-center text-gray-600 mb-4">Room Code: <span className="font-bold text-indigo-600">{roomCode}</span></p>

        {username === host ? (
          <p className="text-center text-indigo-600 font-semibold mb-4">You are the Battle Master üßô‚Äç‚ôÇÔ∏è</p>
        ) : (
          <p className="text-center text-gray-600 mb-4">Waiting for Battle Master to begin...</p>
        )}

        <div className="bg-gray-100 rounded-xl p-4 mb-4">
          <h3 className="text-lg font-semibold text-indigo-700 mb-2">Players:</h3>
          <ul className="grid grid-cols-2 gap-2">
            {players.map((player, index) => (
              <li key={index} className={`py-2 px-4 rounded-lg font-medium ${player === host ? "bg-indigo-100 text-indigo-800 font-bold" : "bg-white text-gray-700"}`}>
                {player === username ? `${player} (You)` : player}
              </li>
            ))}
          </ul>
        </div>

        <div className="text-center mb-6">
          <span className={`text-lg font-semibold ${timeLeft <= 30 ? "text-red-600" : "text-indigo-600"}`}>
            Time left: {Math.floor(timeLeft / 60)}:{(timeLeft % 60).toString().padStart(2, "0")}
          </span>
        </div>

        {roomStatus === "waiting" && username === host && (
          <div className="text-center">
            <button
              onClick={handleStart}
              className="bg-gradient-to-r from-indigo-600 to-purple-600 text-white px-8 py-3 rounded-xl font-semibold hover:shadow-lg hover:scale-105 transition-all duration-200 flex items-center gap-2"
            >
              <span>‚öîÔ∏è</span> Begin Battle
            </button>
          </div>
        )}
      </div>
    </div>
  );
};

export default WaitingRoom;
