"use client"

import React, { useState, useEffect } from "react";
import { useLocation, useNavigate } from "react-router-dom";
import { toast } from "react-toastify";
import Navbar2 from "./Navbar2";
import Editor from "@monaco-editor/react";
import problems from "../data/problems.json";

export default function PracticePage() {
  const location = useLocation()
  const navigate = useNavigate()
  const [problem, setProblem] = useState(null)
  const [code, setCode] = useState("")
  const [language, setLanguage] = useState("java")
  const [output, setOutput] = useState("")
  const [isRunning, setIsRunning] = useState(false)
  const [isSubmitting, setIsSubmitting] = useState(false)
  const [testResults, setTestResults] = useState([])
  const [testCaseIndex, setTestCaseIndex] = useState(0)
  const [testCaseOutput, setTestCaseOutput] = useState("")
  const [testCasePassed, setTestCasePassed] = useState(false)
  const [testCaseInput, setTestCaseInput] = useState("")
  const input = problem?.testCases.map(tc => tc.input) || ""

  // Default starter code templates for different languages
  const starterCodeTemplates = {
    java: `class Solution {
}`
  }

  // Add this mapping at the top of your file or before the runCode function
const languageIdMapping = {
  java: 62,     
};

  useEffect(() => {
    // Get problem from location state or redirect back to problem list
    const problemId = location.state?.problemId;
    if (!problemId) {
      navigate('/problems');
      return;
    }

    // Find the problem in problems.json
    const selectedProblem = problems.find(p => p.title === problemId);
    if (selectedProblem) {
      setProblem(selectedProblem);
      // Set initial starter code based on selected language
      setCode(starterCodeTemplates[language] || '');
    } else {
      toast.error('Problem not found');
      navigate('/problems');
    }
  }, [location, navigate, language])

  // Change language and update starter code
  const handleLanguageChange = (newLanguage) => {
    setLanguage(newLanguage)
    setCode(starterCodeTemplates[newLanguage])
  }

  // Mock function to run code (in a real app, this would send code to a backend)
  const runCode = async () => {
    console.log(code);
    console.log(input);
    setOutput("")
    try {
      const token = sessionStorage.getItem("Authtoken")
      // console.log(token);
      if (!token) throw new Error("Unauthorized")
      const response = await fetch("http://localhost:8788/api/coderun", {
        method: "POST",
        headers: {
          "Content-Type": "application/json",
          "Authorization": `Bearer ${token}`,
        },
        body: JSON.stringify({
          code,
          language,
          testCaseInput
        }),
      });
      const result = await response.json();
      setOutput(result.output);
    } catch (error) {
      toast.error('Error running code');
      console.error(error);
    } finally {
      setIsRunning(false);
    }
  };


  const handleRunCode = async () => {
  setIsRunning(true);
  const token = sessionStorage.getItem("Authtoken");
  setTestResults([]); // Clear previous test results

  if (!token) {
    toast.error("Unauthorized");
    setIsRunning(false);
    return;
  }

  const results = [];
  const testCases = problem.testCases || [];

  for (let i = 0; i < problem.testCases.length; i++) {
    const testCase = problem.testCases[i];

    try {
      const response = await fetch("http://localhost:8788/api/coderun", {
        method: "POST",
        headers: {
          "Content-Type": "application/json",
          "Authorization": `Bearer ${token}`,
        },
        body: JSON.stringify({
          code,
          language,
          testCaseInput // assuming testCase.input exists
        }),
      });

      const result = await response.json();

      // Compare result.output with testCase.expectedOutput (trim to ignore trailing \n etc.)
      const isPassed = result.output.trim() === testCase.expectedOutput.trim();

      results.push({
        input: testCase.input,
        expected: testCase.expectedOutput,
        testCaseOutput: result.output.trim(),
        passed: isPassed,
      });

    } catch (error) {
      console.error(`Error on test case ${i + 1}:`, error);
      results.push({
        input: testCase.input,
        expected: testCase.expectedOutput,
        actual: "Error",
        passed: false,
      });
    }
  }

  // Save all results to state
  setTestResults(results); // You should have this state in your component
  setIsRunning(false);
};




  const handleCodeChange = (newCode)=>{
    setCode(newCode)
  }

  // Mock function to submit and test code
  const submitCode = async () => {
    if (!problem) {
      toast.error('No problem selected');
      return;
    }

    setIsSubmitting(true);

    try {
      const token = localStorage.getItem('token');
      const response = await fetch(`${process.env.REACT_APP_BACKEND_URL}/submissions`, {
        method: 'POST',
        headers: {
          "Content-Type": "application/json",
          "Authorization": `Bearer ${token}`
        },
        body: JSON.stringify({
          problemId: problem.id,
          code,
          language
        })
      });

      const result = await response.json();

      if (result.success) {
        toast.success('Code submitted successfully!');
        // Additional logic for successful submission
      } else {
        toast.error(result.message || 'Submission failed');
      }
    } catch (error) {
      toast.error('Error submitting code');
      console.error(error);
    } finally {
      setIsSubmitting(false);
    }
  };

  const testCode = () => {
    setIsSubmitting(true)
    setTestResults([])

    // Simulate code testing delay
    setTimeout(() => {
      // Mock test results
      const results = problem.testCases.map((testCase, index) => {
        // In a real app, you would actually run the code against the test cases
        const passed = Math.random() > 0.3 // Random pass/fail for demo
        return {
          testCase,
          passed,
          output: passed ? testCase.output : "Wrong output",
        }
      })

      setTestResults(results)
      setIsSubmitting(false)
    }, 1500)
  }

  if (!problem) {
    return <div>Loading...</div>
  }



  return (
    <div className="min-h-screen bg-[#1e1e1e] text-gray-200 flex flex-col">
