                </div>
              )}

              {/* Test Results Tab */}
              {testResults.length > 0 && (
                <div className="mt-4">
                  <h3 className="text-sm font-semibold mb-2 text-gray-400">Test Results:</h3>
                  <div className="space-y-2">
                    {testResults.map((result, idx) => (
                      <div
                        key={idx}
                        className={`p-2 rounded flex items-start ${
                          result.passed
