import { useEffect, useState } from "react";
import { Client } from "@stomp/stompjs";
import { useParams, useNavigate } from "react-router-dom";
import Navbar2 from "./Navbar2";
import "../styling/waitingForRoomJoin.css";

const SOCKET_URL = "ws://localhost:8788/ws"; // Your WebSocket URL

const WaitingRoom = () => {
  const queryParams = new URLSearchParams(window.location.search);
const roomCode = queryParams.get("roomCode");
const name = queryParams.get("name");

console.log("Extracted Room Code:", roomCode);
console.log("Extracted Name:", name);

  const navigate = useNavigate();

  const username = localStorage.getItem("Username"); // Get user's name
  const [players, setPlayers] = useState([]);
  const [timeLeft, setTimeLeft] = useState(120); // Default timer
  const [isLoading, setIsLoading] = useState(true);

  useEffect(() => {
    console.log("üìå Room Code:", roomCode);
  
    if (!roomCode) {
      console.error("‚ùå Room code is undefined! Subscription will fail.");
      return;
    }
  
    const stompClient = new Client({
      brokerURL: SOCKET_URL,
      debug: (str) => console.log("WebSocket Debug:", str),
      onConnect: () => {
        console.log("‚úÖ Connected to WebSocket");
  
        stompClient.subscribe(`/topic/room/${roomCode}`, (message) => {
          console.log("üì© Received Message:", message.body);
          try {
            const updatedRoom = JSON.parse(message.body);
            console.log("üìù Updated Room Data:", updatedRoom);
            
            let updatedPlayers = updatedRoom.players || [];
            if (!updatedPlayers.includes(updatedRoom.host)) {
              updatedPlayers.push(updatedRoom.host);
            }
            console.log("üë• Updated Players:", updatedPlayers);
            
            setPlayers(updatedPlayers);
            setTimeLeft(updatedRoom.timer);
            setIsLoading(false);
          } catch (error) {
            console.error("‚ùå Error parsing message body:", error);
          }
        });
  
        console.log(`üì° Subscribed to /topic/room/${roomCode}`);
      },
    });
  
    stompClient.activate();
  
    return () => {
      stompClient.deactivate();
    };
  }, [roomCode]);
  

  // Countdown Timer (Sync with Host)
  useEffect(() => {
    if (timeLeft <= 0) {
      navigate("/"); // Redirect to home page when time runs out
      return;
    }

    const timer = setInterval(() => {
      setTimeLeft((prev) => prev - 1);
    }, 1000);

    return () => clearInterval(timer);
  }, [timeLeft, navigate]);

  // Handle Cancel Button Click
  const handleCancel = () => {
    navigate("/"); // Redirect to home page
  };

  return (
    <div>
      <Navbar2 />
      <div className="waiting-room">
        {/* User's Name (Instead of Host) */}
        <h2 className="host-title">üë§ Username: {username}</h2>

        {/* Room Code */}
        <p className="room-code">Room Code: <strong>{roomCode}</strong></p>

        {/* Welcome Status */}
        <div className="welcome-status">üéâ Welcome to the Game Room!</div>

        {/* Countdown Timer */}
        <div className="countdown">
          ‚è≥ Test starts in: <span className="timer">{timeLeft}s</span>
        </div>

        {/* Loading Animation */}
        {isLoading && (
          <div className="loading-container">
            <div className="spinner"></div>
            <p className="loading-text">Waiting for players...</p>
          </div>
        )}

        {/* Players List */}
        <h3 className="players-title">Players in Room:</h3>
        <ul className="players-list">
          {players.map((player, index) => (
            <li key={index} className="player-item fade-in">
              {player}
            </li>
          ))}
        </ul>

        {/* Exit Button */}
        <button className="cancel-button" onClick={handleCancel}>
          Exit Room
        </button>
      </div>
    </div>
  );
};

export default WaitingRoom;
