import { useEffect, useState, useRef } from "react";
import { Client } from "@stomp/stompjs";
import { useParams, useNavigate } from "react-router-dom";
import Navbar2 from "./Navbar2";

const SOCKET_URL = "ws://localhost:8788/ws";

const WaitingRoom = () => {
  const { roomCode } = useParams();
  const navigate = useNavigate();
  const [players, setPlayers] = useState([]);
  const [host, setHost] = useState("");
  const [timeLeft, setTimeLeft] = useState(300); // 5 minutes in seconds
  const [isLoading, setIsLoading] = useState(true);
  const [roomStatus, setRoomStatus] = useState("waiting"); // "waiting", "started", "ended"
  const stompClientRef = useRef(null);

  // WebSocket Connection
  useEffect(() => {
    if (!roomCode || stompClientRef.current) return;

    stompClientRef.current = new Client({
      brokerURL: SOCKET_URL,
      reconnectDelay: 5000,
      debug: (msg) => console.log("üêõ STOMP Debug:", msg),
      onConnect: () => {
        console.log("‚úÖ Connected to WebSocket");

        const topic = `/topic/room/${roomCode}`;
        console.log(`üì° Subscribing to: ${topic}`);

        stompClientRef.current.subscribe(topic, (message) => {
          console.log(`üîî Received WebSocket message:`, message.body);
          try {
            const updatedRoom = JSON.parse(message.body);
            console.log("‚úÖ Parsed WebSocket Data:", updatedRoom);

            setHost(updatedRoom.host || "");
            setPlayers((prevPlayers) => {
              const newPlayers = new Set(prevPlayers);
              (updatedRoom.players || []).forEach((player) => newPlayers.add(player));
              if (updatedRoom.host) newPlayers.add(updatedRoom.host);
              return Array.from(newPlayers);
            });
            setRoomStatus(updatedRoom.status || "waiting");
            if (updatedRoom.timer !== undefined) setTimeLeft(updatedRoom.timer);
            setIsLoading(false);
          } catch (error) {
            console.error("‚ùå Error parsing WebSocket message:", error);
          }
        });

        // Publish initial room creation message (assumes user is host)
        stompClientRef.current.publish({
          destination: "/app/room/create",
          body: JSON.stringify({ roomCode, host: localStorage.getItem("username") || "Host" }),
        });
      },
      onStompError: (frame) => console.error("‚ùå WebSocket error:", frame),
      onWebSocketClose: () => console.log("üî¥ WebSocket disconnected"),
    });

    stompClientRef.current.activate();

    return () => {
      if (stompClientRef.current) {
        stompClientRef.current.deactivate();
        stompClientRef.current = null;
      }
    };
  }, [roomCode]);

  // Timer Logic
  useEffect(() => {
    if (timeLeft <= 0 && roomStatus === "waiting") {
      navigate("/");
      return;
    }
    if (roomStatus === "waiting") {
      const timer = setInterval(() => setTimeLeft((prev) => prev - 1), 1000);
      return () => clearInterval(timer);
    }
  }, [timeLeft, roomStatus, navigate]);

  // Handle Start Room
  const handleStart = () => {
    if (stompClientRef.current) {
      stompClientRef.current.publish({
        destination: "/app/room/start",
        body: JSON.stringify({ roomCode, status: "started" }),
      });
      setRoomStatus("started");
    }
  };

  // Handle End Room
  const handleEnd = () => {
    if (stompClientRef.current) {
      stompClientRef.current.publish({
        destination: "/app/room/end",
        body: JSON.stringify({ roomCode, status: "ended" }),
      });
      navigate("/");
    }
  };

  return (
    <div className="min-h-screen bg-gray-100">
      <Navbar2 />
      <div className="container mx-auto px-4 py-8">
        <div className="bg-white shadow-lg rounded-lg p-6 max-w-2xl mx-auto">
          {/* Header */}
          <h1 className="text-3xl font-bold text-gray-800 text-center mb-4">
            Waiting Room
          </h1>
          <p className="text-center text-gray-600 mb-6">
            Room Code: <span className="font-semibold text-indigo-600">{roomCode}</span>
          </p>

          {/* Timer */}
          {roomStatus === "waiting" && (
            <div className="text-center mb-6">
              <p className="text-lg text-gray-700">
                ‚è≥ Test starts in:{" "}
                <span className="font-bold text-indigo-600">
                  {Math.floor(timeLeft / 60)}:{(timeLeft % 60).toString().padStart(2, "0")}
                </span>
              </p>
            </div>
          )}

          {/* Loading State */}
          {isLoading ? (
            <div className="flex flex-col items-center justify-center py-8">
              <div className="w-12 h-12 border-4 border-indigo-600 border-t-transparent rounded-full animate-spin"></div>
              <p className="mt-4 text-gray-600">Waiting for players...</p>
            </div>
          ) : (
            <>
              {/* Host Info */}
              <div className="mb-6">
                <h2 className="text-xl font-semibold text-gray-800">
                  Host: <span className="text-indigo-600">{host || "N/A"}</span>
                </h2>
              </div>

              {/* Players List */}
              <div className="mb-6">
                <h3 className="text-lg font-semibold text-gray-800 mb-2">
                  Players ({players.length}):
                </h3>
                <ul className="space-y-2 max-h-60 overflow-y-auto border border-gray-200 rounded-md p-4">
                  {players.length > 0 ? (
                    players.map((player, index) => (
                      <li
                        key={index}
                        className="text-gray-700 flex items-center space-x-2"
                      >
                        <span className="w-2 h-2 bg-indigo-600 rounded-full"></span>
                        <span>{player === host ? `${player} (Host)` : player}</span>
                      </li>
                    ))
                  ) : (
                    <p className="text-gray-500 italic">No players joined yet.</p>
                  )}
                </ul>
              </div>

              {/* Buttons */}
              <div className="flex justify-center space-x-4">
                {roomStatus === "waiting" && (
                  <button
                    onClick={handleStart}
                    className="bg-indigo-600 text-white px-6 py-2 rounded-md hover:bg-indigo-700 transition duration-200"
                  >
                    Start Room
                  </button>
                )}
                <button
                  onClick={handleEnd}
                  className="bg-red-600 text-white px-6 py-2 rounded-md hover:bg-red-700 transition duration-200"
                >
                  End Room
                </button>
              </div>
            </>
          )}
        </div>
      </div>
    </div>
  );
};

export default WaitingRoom;
