import { useEffect, useState } from "react";
import { Client } from "@stomp/stompjs";
import { useParams, useNavigate } from "react-router-dom";
import styles from "../styling/waitingRoom.css";
import Navbar2 from "./Navbar2";

const SOCKET_URL = "ws://localhost:8788/ws"; // Your WebSocket URL

const WaitingRoom = () => {
  const { roomCode } = useParams();
  const navigate = useNavigate();
  const [players, setPlayers] = useState([]);
  const [timeLeft, setTimeLeft] = useState(120); // 2-minute countdown
  const [isLoading, setIsLoading] = useState(true);

  useEffect(() => {
    const stompClient = new Client({
      brokerURL: SOCKET_URL,
      onConnect: () => {
        console.log("Connected to WebSocket");

        // Subscribe to real-time room updates
        stompClient.subscribe(`/topic/room/${roomCode}`, (message) => {
          const updatedRoom = JSON.parse(message.body);
          setPlayers(updatedRoom.players);
          setIsLoading(false); // Stop loading when first player joins
        });
      },
    });

    stompClient.activate();

    return () => {
      stompClient.deactivate();
    };
  }, [roomCode]);

  // Countdown Timer
  useEffect(() => {
    if (timeLeft <= 0) {
      navigate("/"); // Redirect to home page when time runs out
      return;
    }

    const timer = setInterval(() => {
      setTimeLeft((prev) => prev - 1);
    }, 1000);

    return () => clearInterval(timer);
  }, [timeLeft, navigate]);

  // Handle Cancel Button Click
  const handleCancel = () => {
    navigate("/"); // Redirect to home page
  };

  return (
    <div>
    <Navbar2/>
    <div className="waiting-room">
    
      <h2 className="title">Waiting Room</h2>
      <p className="room-code">Room Code: <strong>{roomCode}</strong></p>

      {/* Countdown Timer */}
      <div className="countdown">
        ‚è≥ Test starts in: <span className="timer">{timeLeft}s</span>
      </div>

      {/* Loading Animation */}
      {isLoading && (
        <div className="loading-container">
          <div className="spinner"></div>
          <p className="loading-text">Waiting for players...</p>
        </div>
      )}

      {/* Players List with Animation */}
      <h3 className="players-title">Players:</h3>
      <ul className="players-list">
        {players.map((player, index) => (
          <li key={index} className="player-item fade-in">
            {player}
          </li>
        ))}
      </ul>

      {/* Cancel Button */}
      <button className="cancel-button" onClick={handleCancel}>
        Exit Room
      </button>
    </div>
    </div>
  );
};

export default WaitingRoom;
