import { useEffect, useState, useRef } from "react";
import { Client } from "@stomp/stompjs";
import { useNavigate } from "react-router-dom";
import Navbar2 from "./Navbar2";
import "../styling/waitingForRoomJoin.css";

const SOCKET_URL = "ws://localhost:8788/ws";

const WaitingRoomJoins = () => {
  const queryParams = new URLSearchParams(window.location.search);
  const roomCode = queryParams.get("roomCode");

  const navigate = useNavigate();
  const [players, setPlayers] = useState([]);
  const [host, setHost] = useState("");
  const [isLoading, setIsLoading] = useState(true);
  const [timeLeft, setTimeLeft] = useState(null);

  const stompClientRef = useRef(null);

  useEffect(() => {
    if (!roomCode) {
      console.error("❌ Room code is missing! Cannot subscribe.");
      return;
    }

    if (!stompClientRef.current) {
      stompClientRef.current = new Client({
        brokerURL: SOCKET_URL,
        reconnectDelay: 5000,
        onConnect: () => {
          console.log("✅ Connected to WebSocket");

          stompClientRef.current.subscribe(`/topic/room/${roomCode}`, (message) => {
            try {
              const updatedRoom = JSON.parse(message.body);

              setHost(updatedRoom.host || "");

              setPlayers((prevPlayers) => {
                const newPlayers = new Set(prevPlayers); 
                (updatedRoom.players || []).forEach((player) => newPlayers.add(player)); // Add new players
                if (updatedRoom.host) newPlayers.add(updatedRoom.host); // Ensure host is added
              
                return Array.from(newPlayers); // Convert Set to array to trigger re-render
              });

              console.log("🔹 Received WebSocket Message:", updatedRoom);
console.log("🔹 Extracted Host:", updatedRoom.host);
console.log("🔹 Extracted Players:", updatedRoom.players);

              

              setTimeLeft(updatedRoom.timer || 0);
              setIsLoading(false);
            } catch (error) {
              console.error("❌ Error parsing WebSocket message:", error);
            }
          });
        },
        onStompError: (frame) => console.error("❌ STOMP error:", frame),
        onWebSocketClose: () => console.log("⚠️ WebSocket disconnected"),
      });

      stompClientRef.current.activate();
    }

    return () => {
      if (stompClientRef.current) {
        stompClientRef.current.deactivate();
        stompClientRef.current = null;
      }
    };
  }, [roomCode]);

  useEffect(() => {
    if (timeLeft === null || timeLeft <= 0) return;

    const timer = setInterval(() => {
      setTimeLeft((prev) => Math.max(prev - 1, 0));
    }, 1000);

    return () => clearInterval(timer);
  }, [timeLeft]);

  const handleCancel = () => {
    navigate("/");
  };

  return (
    <div>
      <Navbar2 />
      <div className="waiting-room">
        <h2 className="host-title">
          👑 Host: <span className="host-name">{host || "Waiting..."}</span>
        </h2>

        <p className="room-code">Room Code: <strong>{roomCode || "N/A"}</strong></p>

        <div className="welcome-status">🎉 Welcome to the Coding Room!</div>

        {timeLeft !== null && (
          <div className="countdown">
            ⏳ Test starts in: <span className="timer">{timeLeft}s</span>
          </div>
        )}

        {isLoading ? (
          <div className="loading-container">
            <div className="spinner"></div>
            <p className="loading-text">Waiting for players...</p>
          </div>
        ) : (
          <>
            <h3 className="players-title">Players in Room ({players.length}):</h3>
            <ul className="players-list">
              {players.length > 0 ? (
                players.map((player, index) => (
                  <li key={index} className="player-item fade-in">
                    {player === host ? `👑 ${player} (Host)` : player}
                  </li>
                ))
              ) : (
                <p>No players have joined yet.</p>
              )}
            </ul>
          </>
        )}

        <button className="cancel-button" onClick={handleCancel}>
          Exit Room
        </button>
      </div>
    </div>
  );
};

export default WaitingRoomJoins;
