function shuffle(arr) {
  return [...arr]
    .map((v) => [Math.random(), v])
    .sort((a, b) => a[0] - b[0])
    .map((a) => a[1])
}

function QuizComponent() {
  const [questionType, setQuestionType] = useState(QUESTION_TYPES[0].value)
  const [numQuestions, setNumQuestions] = useState(10)
  const [questions, setQuestions] = useState([])
  const [currentQuestionIndex, setCurrentQuestionIndex] = useState(0)
  const [selectedAnswer, setSelectedAnswer] = useState("")
  const [score, setScore] = useState(0)
  const [quizStarted, setQuizStarted] = useState(false)
  const [quizFinished, setQuizFinished] = useState(false)
  const [timeLeft, setTimeLeft] = useState(10)
  const timerRef = useRef(null)
  const [shuffledAnswers, setShuffledAnswers] = useState([])
  const [resultSaved, setResultSaved] = useState(false)
  const [saving, setSaving] = useState(false)
  const { toast } = useToast()

  useEffect(() => {
    if (quizStarted && questions.length > 0) {
      setShuffledAnswers(
        shuffle([...(questions[currentQuestionIndex].options || questions[currentQuestionIndex].answers || [])]),
      )
      setTimeLeft(10)
      setSelectedAnswer("")
    }
  }, [quizStarted, currentQuestionIndex, questions])

  useEffect(() => {
    if (!quizStarted || quizFinished) return
    if (timeLeft > 0) {
      timerRef.current = setTimeout(() => setTimeLeft(timeLeft - 1), 1000)
    } else {
      handleNextQuestion()
    }
    return () => clearTimeout(timerRef.current)
  }, [quizStarted, quizFinished, timeLeft])

  const handleStart = () => {
    const all = QUESTIONS_MAP[questionType] || []
    const selected = shuffle(all).slice(0, Math.min(numQuestions, all.length))
    setQuestions(selected)
    setCurrentQuestionIndex(0)
    setScore(0)
    setQuizFinished(false)
    setQuizStarted(true)
    setResultSaved(false)
  }

  const handleAnswer = (ans) => {
    setSelectedAnswer(ans)
    if (
      ans === questions[currentQuestionIndex].correctAnswer ||
      ans === questions[currentQuestionIndex].correct_answer
    ) {
      setScore((s) => s + 1)
    }
    setTimeout(() => handleNextQuestion(), 800)
  }

  const handleNextQuestion = () => {
    if (currentQuestionIndex < questions.length - 1) {
      setCurrentQuestionIndex((i) => i + 1)
    } else {
      setQuizFinished(true)
      setQuizStarted(false)
    }
  }

  const handleRestart = () => {
    setQuizStarted(false)
    setQuizFinished(false)
    setQuestions([])
    setCurrentQuestionIndex(0)
    setScore(0)
    setTimeLeft(10)
    setSelectedAnswer("")
    setResultSaved(false)
  }

  useEffect(() => {
    if (quizFinished && !resultSaved && questions.length > 0) {
      setSaving(true)
      const username = localStorage.getItem("Username") || "anonymous"
      const now = new Date()
      const date = now.toISOString().split("T")[0]
      const time = now.toTimeString().split(" ")[0]
      const finalScore = score

      // Uncomment this for actual API call

      fetch('http://localhost:8788/api/quiz/saveResult', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ username, score: finalScore, date, time, No_of_questions: questions.length })
      })
        .then(res => res.text())
        .then(text => {
          setResultSaved(true);
          setSaving(false);
          toast({
            title: "Result saved",
            description: `Your score of ${finalScore}/${questions.length} has been saved.`,
          });
        })
        .catch(() => {
          setSaving(false);
          toast({
            title: "Error saving result",
            description: "There was a problem saving your quiz result.",
            variant: "destructive",
          });
        });


      // Simulate API call (replace with your actual API endpoint)
      setTimeout(() => {
        setSaving(false)
        setResultSaved(true)
        toast({
          title: "Result saved",
          description: `Your score of ${finalScore}/${questions.length} has been saved.`,
        })
      }, 1000)
    }
  }, [quizFinished, resultSaved, questions, score, toast])

  const progressPercentage = quizStarted ? ((currentQuestionIndex + 1) / questions.length) * 100 : 0
  const timePercentage = (timeLeft / 10) * 100

  return (
    <div className="min-h-screen bg-gradient-to-b from-slate-50 to-slate-100 dark:from-slate-950 dark:to-slate-900">
      <Navbar />
      <main className="container max-w-3xl mx-auto px-4 py-8">
        <h1 className="text-3xl font-bold text-center mb-8 text-transparent bg-clip-text bg-gradient-to-r from-purple-600 to-pink-600">
          Interactive Quiz
        </h1>

        {!quizStarted && !quizFinished && (
          <Card className="shadow-lg border-0 overflow-hidden bg-white dark:bg-slate-900">
            <div className="h-2 bg-gradient-to-r from-purple-500 to-pink-500"></div>
            <CardContent className="p-6">
              <h2 className="text-2xl font-semibold mb-6 text-center">Select Quiz Options</h2>

              <div className="space-y-6">
                <div className="space-y-2">
                  <Label htmlFor="question-type">Question Type</Label>
                  <Select value={questionType} onValueChange={setQuestionType}>
                    <SelectTrigger id="question-type" className="w-full">
                      <SelectValue placeholder="Select question type" />
                    </SelectTrigger>
                    <SelectContent>
                      {QUESTION_TYPES.map((type) => (
                        <SelectItem key={type.value} value={type.value}>
                          {type.label}
                        </SelectItem>
                      ))}
                    </SelectContent>
                  </Select>
                </div>

                <div className="space-y-2">
                  <Label htmlFor="num-questions">Number of Questions</Label>
                  <Select value={numQuestions.toString()} onValueChange={(value) => setNumQuestions(Number(value))}>
                    <SelectTrigger id="num-questions" className="w-full">
                      <SelectValue placeholder="Select number of questions" />
                    </SelectTrigger>
                    <SelectContent>
                      {[5, 10, 15, 20].map((n) => (
                        <SelectItem key={n} value={n.toString()}>
                          {n}
                        </SelectItem>
                      ))}
                    </SelectContent>
                  </Select>
                </div>

                <Button
                  onClick={handleStart}
                  className="w-full bg-gradient-to-r from-purple-600 to-pink-600 hover:from-purple-700 hover:to-pink-700 transition-all"
                  size="lg"
                >
                  Start Quiz
                </Button>
              </div>
            </CardContent>
          </Card>
        )}

        {quizStarted && !quizFinished && questions.length > 0 && (
          <Card className="shadow-xl border-0 overflow-hidden bg-white dark:bg-slate-900">
            <div className="h-2 bg-gradient-to-r from-purple-500 to-pink-500"></div>
            <CardContent className="p-6">
              <div className="flex items-center justify-between mb-4">
                <div className="flex items-center space-x-2">
                  <span className="inline-flex items-center justify-center w-8 h-8 rounded-full bg-purple-100 text-purple-700 font-semibold dark:bg-purple-900 dark:text-purple-100">
                    {currentQuestionIndex + 1}
                  </span>
                  <span className="text-sm text-slate-500 dark:text-slate-400">of {questions.length}</span>
                </div>
                <div className="bg-purple-100 dark:bg-purple-900 px-3 py-1 rounded-full">
                  <span className="text-sm font-medium text-purple-700 dark:text-purple-100">Score: {score}</span>
                </div>
              </div>

              <Progress value={progressPercentage} className="h-2 mb-6" />

              <div className="mb-6">
                <h3 className="text-xl font-semibold mb-4 text-slate-800 dark:text-slate-100">
                  {questions[currentQuestionIndex].question}
                </h3>

                <RadioGroup value={selectedAnswer} onValueChange={handleAnswer} className="space-y-3">
                  {shuffledAnswers.map((answer, index) => {
                    const isCorrect =
                      selectedAnswer &&
                      (answer === questions[currentQuestionIndex].correctAnswer ||
                        answer === questions[currentQuestionIndex].correct_answer)
                    const isSelected = selectedAnswer === answer
                    const isWrong = isSelected && !isCorrect

                    return (
                      <div
                        key={index}
                        className={`relative flex items-center rounded-lg border p-4 transition-all ${
                          !selectedAnswer ? "hover:bg-slate-50 dark:hover:bg-slate-800" : ""
                        } ${
                          isCorrect
                            ? "border-green-200 bg-green-50 dark:border-green-900 dark:bg-green-900/20"
                            : isWrong
                              ? "border-red-200 bg-red-50 dark:border-red-900 dark:bg-red-900/20"
                              : "border-slate-200 dark:border-slate-700"
                        }`}
                      >
                        <RadioGroupItem
                          value={answer}
                          id={`answer-${index}`}
                          disabled={!!selectedAnswer}
                          className="mr-3"
                        />
                        <Label
                          htmlFor={`answer-${index}`}
                          className={`flex-grow cursor-pointer ${
                            isCorrect
                              ? "text-green-700 dark:text-green-400"
                              : isWrong
                                ? "text-red-700 dark:text-red-400"
                                : ""
                          }`}
                        >
                          {answer}
                        </Label>
                        {isCorrect && <CheckCircle className="h-5 w-5 text-green-600 dark:text-green-400 ml-2" />}
                        {isWrong && <XCircle className="h-5 w-5 text-red-600 dark:text-red-400 ml-2" />}
                      </div>
                    )
                  })}
                </RadioGroup>
              </div>

              <div className="flex items-center space-x-3 mb-2">
                <Clock className="h-5 w-5 text-purple-600 dark:text-purple-400" />
                <Progress value={timePercentage} className="h-2 flex-grow" />
                <span className="text-sm font-medium">{timeLeft}s</span>
              </div>

              <p className="text-xs text-slate-500 dark:text-slate-400 text-center">
                Auto-advancing to next question in {timeLeft} seconds
              </p>
            </CardContent>
          </Card>
        )}

        {quizFinished && (
          <Card className="shadow-xl border-0 overflow-hidden bg-white dark:bg-slate-900">
            <div className="h-2 bg-gradient-to-r from-purple-500 to-pink-500"></div>
            <CardContent className="p-6 text-center">
              <div className="flex justify-center mb-6">
                <div className="inline-flex items-center justify-center w-24 h-24 rounded-full bg-purple-100 dark:bg-purple-900">
                  <Trophy className="h-12 w-12 text-purple-600 dark:text-purple-400" />
                </div>
              </div>

              <h2 className="text-2xl font-bold mb-2">Quiz Completed!</h2>

              <div className="mb-6">
                <p className="text-4xl font-bold text-purple-600 dark:text-purple-400 mb-1">
                  {score} / {questions.length}
                </p>
                <p className="text-slate-500 dark:text-slate-400">
                  {Math.round((score / questions.length) * 100)}% Correct
                </p>
              </div>

              {saving && <p className="text-sm text-slate-500 dark:text-slate-400 mb-4">Saving your results...</p>}

              <Button
                onClick={handleRestart}
                className="bg-gradient-to-r from-purple-600 to-pink-600 hover:from-purple-700 hover:to-pink-700 transition-all"
                size="lg"
              >
                Try Again
              </Button>
            </CardContent>
          </Card>
        )}
      </main>
    </div>
  )
}

