import { useEffect, useState, useRef } from "react";
import { Client } from "@stomp/stompjs";
import { useNavigate } from "react-router-dom";
import Navbar2 from "./Navbar2";
import "./WaitingRoom.css";

const SOCKET_URL = "ws://localhost:8788/ws";

const WaitingRoomJoins = () => {
  const queryParams = new URLSearchParams(window.location.search);
  const roomCode = queryParams.get("roomCode");
  const username = localStorage.getItem("username");
  const navigate = useNavigate();

  // Redirect if no username
  if (!username) {
    navigate('/');
    return;
  }
  const [players, setPlayers] = useState([]);
  const [host, setHost] = useState("");
  const [isLoading, setIsLoading] = useState(true);
  const [timeLeft, setTimeLeft] = useState(null);
  const [roomStatus, setRoomStatus] = useState("waiting"); // "waiting", "started", "ended"

  const stompClientRef = useRef(null);

  useEffect(() => {
    if (!roomCode) {
      console.error("❌ Room code is missing! Cannot subscribe.");
      return;
    }

    if (!stompClientRef.current) {
      stompClientRef.current = new Client({
        brokerURL: SOCKET_URL,
        reconnectDelay: 5000,
        onConnect: () => {
          console.log("✅ Connected to WebSocket", SOCKET_URL);

          stompClientRef.current.subscribe(`/topic/room/${roomCode}`, (message) => {
            console.log(`✅ Subscribed to: /topic/room/${roomCode}`);

            try {
              console.log("Raw WebSocket Message:", message.body);
              const updatedRoom = JSON.parse(message.body);

              console.log("🔹 WebSocket Data:", updatedRoom);

              // Update the host
              setHost(updatedRoom.host || "");
              setPlayers((prevPlayers) => {
                const newPlayers = new Set(prevPlayers);
                (updatedRoom.players || []).forEach((player) => newPlayers.add(player));
                if (updatedRoom.host) newPlayers.add(updatedRoom.host);
                return Array.from(newPlayers);
              });
              setRoomStatus(updatedRoom.status || "waiting");
              if (updatedRoom.timer !== undefined) setTimeLeft(updatedRoom.timer);
              setIsLoading(false);
            } catch (error) {
              console.error("❌ Error parsing WebSocket message:", error);
            }
          });

          // Publish join room message
          stompClientRef.current.publish({
            destination: "/app/room/join",
            body: JSON.stringify({ 
              roomCode, 
              player: username
            }),
          });
        },
        
        onStompError: (frame) => console.error("❌ STOMP error:", frame),
        onWebSocketClose: () => console.log("⚠️ WebSocket disconnected"),
      });

      stompClientRef.current.activate();
    }

    return () => {
      if (stompClientRef.current) {
        stompClientRef.current.deactivate();
        stompClientRef.current = null;
      }
    };
  }, [roomCode]);

  // Timer countdown logic
  useEffect(() => {
    if (timeLeft !== null && timeLeft <= 0 && roomStatus === "waiting") {
      navigate("/");
      return;
    }
  }, [timeLeft, roomStatus, navigate]);

  const handleCancel = () => {
    navigate("/");
  };

  return (
    <div className="min-h-screen bg-gradient-to-br from-indigo-50 via-white to-indigo-50">
      <Navbar2 />
      <div className="container mx-auto px-4 py-8">
        <div className="bg-white backdrop-blur-sm bg-opacity-90 shadow-xl rounded-2xl p-8 max-w-2xl mx-auto border border-indigo-100 transition-all duration-300 hover:shadow-2xl">
          <h1 className="text-4xl font-extrabold text-transparent bg-clip-text bg-gradient-to-r from-indigo-600 to-purple-600 text-center mb-2 animate-fade-in">
            Welcome to the CodeClash
          </h1>
          <p className="text-center text-gray-600 mb-8 flex items-center justify-center gap-2">
            <span className="px-4 py-1 bg-indigo-50 rounded-full border border-indigo-100">Room Code: 
              <span className="font-semibold text-indigo-600 ml-1">{roomCode || "N/A"}</span>
            </span>
          </p>

          {roomStatus === "waiting" && (
            <div className="text-center mb-8 bg-indigo-50 rounded-xl p-4 border border-indigo-100 animate-pulse-slow">
              <p className="text-lg text-gray-700 flex items-center justify-center gap-2">
                <span className="animate-spin-slow">⏳</span>
                Test starts in:{" "}
                <span className="font-bold text-indigo-600 tabular-nums">
                  {Math.floor(timeLeft / 60)}:{(timeLeft % 60).toString().padStart(2, "0")}
                </span>
              </p>
            </div>
          )}

          {isLoading ? (
            <div className="flex flex-col items-center justify-center py-12">
              <div className="relative">
                <div className="w-16 h-16 border-4 border-indigo-200 border-t-indigo-600 rounded-full animate-spin"></div>
                <div className="w-16 h-16 border-4 border-transparent border-b-purple-400 rounded-full animate-spin absolute top-0 animate-spin-slow"></div>
              </div>
              <p className="mt-6 text-gray-600 animate-pulse">Waiting for players...</p>
            </div>
          ) : (
          <>
            <div className="mb-8 bg-gradient-to-r from-indigo-50 to-purple-50 p-4 rounded-xl border border-indigo-100">
              <h2 className="text-xl font-semibold text-gray-800 flex items-center gap-2">
                <span className="text-indigo-500">👑</span>
                Host: <span className="text-indigo-600 font-bold">{host || "Waiting..."}</span>
              </h2>
            </div>

            <div className="mb-8">
              <h3 className="text-lg font-semibold text-gray-800 mb-3 flex items-center gap-2">
                <span className="text-indigo-500">👥</span>
                Players ({players.length}):
              </h3>
              <ul className="space-y-2 max-h-60 overflow-y-auto border border-indigo-100 rounded-xl p-4 bg-white shadow-inner">
                {players.length > 0 ? (
                  players.map((player, index) => (
                    <li
                      key={index}
                      className="text-gray-700 flex items-center space-x-2 p-2 hover:bg-indigo-50 rounded-lg transition-colors duration-200"
                    >
                      <span className="w-2 h-2 bg-indigo-600 rounded-full animate-pulse"></span>
                      <span>{player === host ? `${player} (Host)` : player}</span>
                    </li>
                  ))
                ) : (
                  <p className="text-gray-500 italic">No players have joined yet.</p>
                )}
              </ul>
            </div>
          </>
        )}

          <div className="flex justify-center">
            <button
              onClick={handleCancel}
              className="bg-gradient-to-r from-red-500 to-red-600 text-white px-8 py-3 rounded-xl hover:shadow-lg hover:scale-105 transition duration-200 font-semibold"
            >
              Exit Room
            </button>
          </div>
        </div>
      </div>
    </div>
  );
};

export default WaitingRoomJoins;
