import React, { useState } from 'react'

export default function CodeEditor() {
  const [code, setCode] = useState(` // You are using JavaScript
  `)

  const [language, setLanguage] = useState("JavaScript")
  const templates = {
    JavaScript: `You are using JavaScript`,
    Python: `You are using Python`,
    Java: `You are using Java`,
    "C++": `You are using C++`
  };
  const [selectedExample, setSelectedExample] = useState(0);
  const [theme, setTheme] = useState("light")
  const [problem, setProblem] = useState({
    title: "1. Two Sum",
    difficulty: "Easy",
    description:
      "Given an array of integers nums and an integer target, return indices of the two numbers such that they add up to target.",
    examples: [
      {
        input: "nums = [2,7,11,15], target = 9",
        output: "[0,1]",
        explanation: "Because nums[0] + nums[1] == 9, we return [0, 1].",
      },
      {
        input: "nums = [3,2,4], target = 6",
        output: "[1,2]",
        explanation: "Because nums[1] + nums[2] == 6, we return [1, 2].",
      },
    ],
  })

  const [activeTab, setActiveTab] = useState("description")
  const [output, setOutput] = useState("")
  const [isRunning, setIsRunning] = useState(false)

  const handleCodeChange = (e) => {
    setCode(e.target.value)
  }

  const runCode = async () => {
    setIsRunning(true);
    setOutput("");
    const example = problem.examples[selectedExample];
    const args = example.input.split(',').map(part => part.split('=')[1].trim());
    if (language === "JavaScript") {
      try {
        const func = new Function(`
          ${code}
          return twoSum(${args.join(', ')});
        `);
        const result = func();
        setOutput(`Output: [${result}]\nExecution successful!`);
      } catch (error) {
        setOutput(`Error: ${error.message}`);
      } finally {
        setIsRunning(false);
      }
    } else {
      try {
        const response = await fetch("http://localhost:8788/api/run", {
          method: "POST",
          headers: { "Content-Type": "application/json" },
          body: JSON.stringify({ code, language, input: example.input })
        });
        const data = await response.json();
        if (data.error) {
          setOutput(`Error: ${data.error}`);
        } else {
          setOutput(`Output: ${data.output}\nExecution successful!`);
        }
      } catch (error) {
        setOutput(`Error: ${error.message}`);
      } finally {
        setIsRunning(false);
      }
    }
  }

  const lineNumbers = code
    .split("\n")
    .map((_, index) => index + 1)
    .join("\n")

  return (
    <div
      style={{
        fontFamily: "Arial, sans-serif",
        display: "flex",
        flexDirection: "column",
        height: "100vh",
        width: "100%",
        overflow: "hidden",
        backgroundColor: theme === "dark" ? "#1e1e1e" : "#f5f5f5",
        color: theme === "dark" ? "#e0e0e0" : "#333",
      }}
    >
      {/* Header */}
      <div
        style={{
          display: "flex",
          alignItems: "center",
          padding: "8px 16px",
          borderBottom: "1px solid #ddd",
          backgroundColor: theme === "dark" ? "#2d2d2d" : "#fff",
        }}
      >
        <div style={{ display: "flex", alignItems: "center" }}>
          <button
            style={{
              backgroundColor: "transparent",
              border: "none",
              cursor: "pointer",
              marginRight: "10px",
              color: theme === "dark" ? "#e0e0e0" : "#333",
            }}
          >
            &#8592;
          </button>
          <span style={{ fontWeight: "bold" }}>Problem List</span>
        </div>

        <div style={{ marginLeft: "auto", display: "flex", gap: "10px" }}>
          <button
            onClick={runCode}
            style={{
              backgroundColor: isRunning ? "#ccc" : "#4caf50",
              color: "white",
              border: "none",
              borderRadius: "4px",
              padding: "6px 12px",
              cursor: isRunning ? "not-allowed" : "pointer",
              display: "flex",
              alignItems: "center",
              gap: "5px",
            }}
            disabled={isRunning}
          >
            {isRunning ? "Running..." : "Run"}
          </button>

          <button
            style={{
              backgroundColor: "#2196f3",
              color: "white",
              border: "none",
              borderRadius: "4px",
              padding: "6px 12px",
              cursor: "pointer",
            }}
          >
            Submit
          </button>

          <button
            onClick={() => setTheme(theme === "dark" ? "light" : "dark")}
            style={{
              backgroundColor: "transparent",
              border: "1px solid #ddd",
              borderRadius: "4px",
              padding: "6px 12px",
              cursor: "pointer",
              color: theme === "dark" ? "#e0e0e0" : "#333",
            }}
          >
            {theme === "dark" ? "☀️" : "🌙"}
          </button>
        </div>
        <div style={{ marginLeft: "20px" }}>
          <label>
            Test Case:
            <select
              value={selectedExample}
              onChange={e => setSelectedExample(Number(e.target.value))}
              style={{ marginLeft: "8px" }}
            >
              {problem.examples.map((ex, idx) => (
                <option key={idx} value={idx}>{`Example ${idx+1}`}</option>
              ))}
            </select>
          </label>
        </div>
      </div>

      {/* Main Content */}
      <div
        style={{
          display: "flex",
          flex: 1,
          overflow: "hidden",
        }}
      >
        {/* Left Panel */}
        <div
          style={{
            width: "50%",
            borderRight: "1px solid #ddd",
            display: "flex",
            flexDirection: "column",
            overflow: "hidden",
          }}
        >
          {/* Tabs */}
          <div
            style={{
              display: "flex",
              borderBottom: "1px solid #ddd",
              backgroundColor: theme === "dark" ? "#2d2d2d" : "#fff",
            }}
          >
            {["description", "solution", "submissions"].map((tab) => (
              <button
                key={tab}
                onClick={() => setActiveTab(tab)}
                style={{
                  padding: "10px 15px",
                  border: "none",
                  backgroundColor: activeTab === tab ? (theme === "dark" ? "#3d3d3d" : "#f0f0f0") : "transparent",
                  borderBottom: activeTab === tab ? "2px solid #2196f3" : "none",
                  cursor: "pointer",
                  color: theme === "dark" ? "#e0e0e0" : "#333",
                  textTransform: "capitalize",
                }}
              >
                {tab}
              </button>
            ))}
          </div>

          {/* Tab Content */}
          <div
            style={{
              flex: 1,
              padding: "16px",
              overflow: "auto",
              backgroundColor: theme === "dark" ? "#1e1e1e" : "#fff",
            }}
          >
            {activeTab === "description" && (
              <div>
                <h2>{problem.title}</h2>
                <span
                  style={{
                    display: "inline-block",
                    padding: "2px 8px",
                    backgroundColor: "#4caf50",
                    color: "white",
                    borderRadius: "12px",
                    fontSize: "12px",
                    marginBottom: "16px",
                  }}
                >
                  {problem.difficulty}
                </span>

                <p>{problem.description}</p>

                <div style={{ marginTop: "20px" }}>
                  {problem.examples.map((example, index) => (
                    <div key={index} style={{ marginBottom: "16px" }}>
                      <h4>Example {index + 1}:</h4>
                      <div
                        style={{
                          backgroundColor: theme === "dark" ? "#2d2d2d" : "#f5f5f5",
                          padding: "10px",
                          borderRadius: "4px",
                          fontFamily: "monospace",
                        }}
                      >
                        <div>
                          <strong>Input:</strong> {example.input}
                        </div>
                        <div>
                          <strong>Output:</strong> {example.output}
                        </div>
                        {example.explanation && (
                          <div>
                            <strong>Explanation:</strong> {example.explanation}
                          </div>
                        )}
                      </div>
                    </div>
                  ))}
                </div>
              </div>
            )}

            {activeTab === "solution" && (
              <div>
                <h3>Solution</h3>
                <p>This is where you would see official solutions after submitting your answer.</p>
              </div>
            )}

            {activeTab === "submissions" && (
              <div>
                <h3>Your Submissions</h3>
                <p>Your previous submissions will appear here.</p>
              </div>
            )}
          </div>
        </div>

        {/* Right Panel - Code Editor */}
        <div
          style={{
            width: "50%",
            display: "flex",
            flexDirection: "column",
            overflow: "hidden",
          }}
        >
          {/* Editor Header */}
          <div
            style={{
              display: "flex",
              alignItems: "center",
              padding: "8px 16px",
              borderBottom: "1px solid #ddd",
              backgroundColor: theme === "dark" ? "#2d2d2d" : "#fff",
            }}
          >
            <div style={{ fontWeight: "bold" }}>Code</div>
            <div style={{ marginLeft: "16px" }}>
              <select
                value={language}
                onChange={(e) => { setLanguage(e.target.value); setCode(templates[e.target.value] || ""); }}
                style={{
                  padding: "4px 8px",
                  borderRadius: "4px",
                  border: "1px solid #ddd",
                  backgroundColor: theme === "dark" ? "#3d3d3d" : "#fff",
                  color: theme === "dark" ? "#e0e0e0" : "#333",
                }}
              >
                <option value="JavaScript">JavaScript</option>
                <option value="Python">Python</option>
                <option value="Java">Java</option>
                <option value="C++">C++</option>
              </select>
            </div>
          </div>

          {/* Code Editor */}
          <div
            style={{
              flex: 1,
              display: "flex",
              overflow: "hidden",
              backgroundColor: theme === "dark" ? "#1e1e1e" : "#f8f8f8",
              position: "relative",
            }}
          >
            {/* Line Numbers */}
            <div
              style={{
                width: "40px",
                padding: "10px 8px",
                textAlign: "right",
                color: theme === "dark" ? "#6a737d" : "#999",
                backgroundColor: theme === "dark" ? "#252525" : "#f0f0f0",
                fontFamily: "monospace",
                userSelect: "none",
                overflow: "hidden",
                borderRight: "1px solid #ddd",
                whiteSpace: "pre-line",
              }}
            >
              {lineNumbers}
            </div>

            {/* Code Input */}
            <textarea
              value={code}
              onChange={handleCodeChange}
              style={{
                flex: 1,
                padding: "10px",
                border: "none",
                outline: "none",
                resize: "none",
                fontFamily: "monospace",
                fontSize: "14px",
                lineHeight: "1.5",
                backgroundColor: theme === "dark" ? "#1e1e1e" : "#fff",
                color: theme === "dark" ? "#e0e0e0" : "#333",
                whiteSpace: "pre",
                overflowWrap: "normal",
                overflowX: "auto",
              }}
              spellCheck="false"
            />
          </div>

          {/* Output Console */}
          <div
            style={{
              height: "120px",
              borderTop: "1px solid #ddd",
              overflow: "auto",
              padding: "10px",
              fontFamily: "monospace",
              backgroundColor: theme === "dark" ? "#252525" : "#f0f0f0",
              color: output.includes("Error") ? "#f44336" : theme === "dark" ? "#4caf50" : "#2e7d32",
              whiteSpace: "pre-wrap",
            }}
          >
            {output || "Run your code to see output here..."}
          </div>
        </div>
      </div>

      {/* Footer */}
      <div
        style={{
          padding: "8px 16px",
          borderTop: "1px solid #ddd",
          display: "flex",
          justifyContent: "space-between",
          backgroundColor: theme === "dark" ? "#2d2d2d" : "#fff",
          fontSize: "12px",
        }}
      >
        <div>
          <span>Likes: 61.3K</span>
          <span style={{ margin: "0 10px" }}>•</span>
          <span>Dislikes: 1.3K</span>
        </div>
        <div>
          <span>1983 Online</span>
        </div>
      </div>
    </div>
  )
}
